   125→
   126→// ============================================================================
   127→// DEBOUNCED UI UPDATES (Performance: batch DOM updates)
   128→// ============================================================================
   129→function scheduleUIUpdate(callback) {
   130→  if (pendingUIUpdate) {
   131→    cancelAnimationFrame(pendingUIUpdate);
   132→  }
   133→  pendingUIUpdate = requestAnimationFrame(() => {
   134→    callback();
   135→    pendingUIUpdate = null;
