   148→
   149→// ============================================================================
   150→// KEYBOARD SHORTCUTS
   151→// ============================================================================
   152→function initKeyboardShortcuts() {
   153→  document.addEventListener('keydown', (e) => {
   154→    // Only handle when combined preview is visible
   155→    const previewVisible = ui.combinedPreview?.style.display !== 'none' &&
   156→                           ui.combinedPreview?.style.display !== '';
   157→
   158→    if (!previewVisible) return;
   159→
   160→    const isMac = navigator.platform.toUpperCase().indexOf('MAC') >= 0;
   161→    const cmdKey = isMac ? e.metaKey : e.ctrlKey;
   162→
   163→    // Enter - Apply selected
   164→    if (e.key === 'Enter' && !e.shiftKey) {
   165→      e.preventDefault();
   166→      if (selectedSilenceIndices.size > 0) {
   167→        ui.applyPreviewBtn?.click();
   168→      }
   169→    }
   170→
   171→    // Escape - Cancel
   172→    if (e.key === 'Escape') {
   173→      e.preventDefault();
   174→      ui.cancelPreviewBtn?.click();
   175→    }
   176→
   177→    // Cmd/Ctrl+A - Select all
   178→    if (e.key === 'a' && cmdKey) {
   179→      e.preventDefault();
   180→      if (ui.selectAllSilences) {
   181→        ui.selectAllSilences.checked = true;
   182→        selectAllSilences(true);
   183→      }
   184→    }
   185→
   186→    // Cmd/Ctrl+D - Deselect all
   187→    if (e.key === 'd' && cmdKey) {
   188→      e.preventDefault();
   189→      if (ui.selectAllSilences) {
   190→        ui.selectAllSilences.checked = false;
   191→        selectAllSilences(false);
   192→      }
   193→    }
   194→
   195→    // Cmd/Ctrl+I - Invert selection
   196→    if (e.key === 'i' && cmdKey) {
   197→      e.preventDefault();
   198→      invertSilenceSelection();
   199→    }
