   201→}
   202→
   203→// ============================================================================
   204→// UNIFIED WORKFLOW (Silence Detection + Takes Detection)
   205→// ============================================================================
   206→function initUnifiedWorkflow() {
   207→  if (!ui.goBtn) return;
   208→
   209→  ui.goBtn.addEventListener('click', async () => {
   210→    // Prevent double-click
   211→    if (isOperationInProgress) {
   212→      setStatus('Operation in progress...');
   213→      return;
   214→    }
   215→
   216→    isOperationInProgress = true;
   217→    ui.goBtn.disabled = true;
   218→
   219→    try {
   220→      showProgress('Preparing audio...');
   221→
   222→      // Step 1: Export audio
   223→      const exportSuccess = await exportAudioInternal();
   224→      if (!exportSuccess) {
   225→        throw new Error('Audio export failed');
   226→      }
   227→
   228→      // Step 2: Get settings
   229→      const sensitivity = parseInt(ui.sensitivitySlider?.value || 50);
   230→      const useIsolated = ui.sourceIsolated?.checked ?? false;
   231→      const detectTakes = ui.enableTakesDetection?.checked ?? true;
   232→
   233→      // Step 3: Vocal isolation if needed
   234→      let audioPath = WAV_PATH;
   235→      if (useIsolated) {
   236→        // Check isolation access before proceeding
   237→        const credits = getCurrentCredits();
   238→        if (!credits || !credits.hasIsolationAccess) {
   239→          throw new Error('Vocal isolation requires Pro or Team tier. Upgrade to access this feature.');
   240→        }
   241→
   242→        // Show overage warning if applicable
   243→        const isolationMins = credits.isolationHoursRemaining * 60;
   244→        if (isolationMins <= 0) {
   245→          const overageRate = credits.isolationOverageRate || 0.08;
   246→          showProgress(`Isolating vocals (overage: $${overageRate}/min)...`);
   247→        } else {
   248→          showProgress(`Isolating vocals (${isolationMins.toFixed(0)} min remaining)...`);
   249→        }
   250→
   251→        const isolateResult = await isolateVocals();
   252→        if (isolateResult.success) {
   253→          audioPath = isolateResult.outputPath;
   254→          // Update credits display after isolation
   255→          await refreshCredits();
   256→        } else {
   257→          throw new Error(isolateResult.error || 'Vocal isolation failed');
   258→        }
   259→      }
   260→
   261→      // Step 4: Run detection (parallel if takes enabled)
   262→      const params = mapSensitivity(sensitivity);
   263→      let silences = [];
   264→      let takes = [];
   265→
   266→      if (detectTakes) {
   267→        // Parallel detection for performance
   268→        showProgress('Detecting silences and analyzing takes...');
   269→        const [silenceResult, transcriptResult] = await Promise.all([
   270→          detectSilences(audioPath, params),
   271→          transcribeAudio()
   272→        ]);
   273→
   274→        silences = silenceResult;
   275→
   276→        // Extract takes from transcript result
   277→        if (transcriptResult.success && transcriptResult.takes) {
   278→          if (transcriptResult.takes.takes) {
   279→            takes = transcriptResult.takes.takes;
   280→          } else if (Array.isArray(transcriptResult.takes)) {
   281→            takes = transcriptResult.takes;
   282→          }
   283→        }
   284→
   285→        // Store transcript for reference
   286→        window.currentTranscript = transcriptResult.transcript;
   287→      } else {
   288→        // Just detect silences
   289→        showProgress('Detecting silences...');
   290→        silences = await detectSilences(audioPath, params);
   291→      }
   292→
   293→      if (silences.length === 0) {
   294→        showEmptyState();
   295→        setStatus('No silences detected. Try increasing sensitivity.');
   296→        return;
   297→      }
   298→
   299→      // Step 5: Filter silences by takes (protect speech)
   300→      const filtered = filterSilencesByTakes(silences, takes);
   301→
   302→      // Step 6: Add timeline markers (clear old ones first)
   303→      showProgress('Adding timeline markers...');
   304→      await clearSpliceMarkers();
   305→      const markerResult = await addTimelineMarkers(filtered.safe, filtered.protected, takes);
   306→
   307→      // Step 7: Show combined preview
   308→      showCombinedPreview(filtered.safe, filtered.protected, takes);
   309→
   310→      const markerInfo = markerResult.added > 0 ? ` (${markerResult.added} markers added)` : '';
   311→      const statusMsg = takes.length > 0
   312→        ? `Found ${silences.length} silences, ${filtered.protected.length} protected by ${takes.length} takes${markerInfo}`
   313→        : `Found ${silences.length} silence(s) - review and apply${markerInfo}`;
   314→      setStatus(statusMsg);
   315→
   316→    } catch (err) {
   317→      showEmptyState();
   318→      setStatus('Error: ' + err.message);
   319→      console.error('[SPLICE] Unified workflow error:', err);
   320→    } finally {
   321→      isOperationInProgress = false;
   322→      ui.goBtn.disabled = false;
   323→    }
   324→  });
   325→}
   326→
   327→// ============================================================================
   328→// TAKE-AWARE SILENCE FILTERING
   329→// ============================================================================
   330→/**
   331→ * Filter silences to protect those that overlap with takes (speech segments).
   332→ * Returns { safe: [], protected: [] }
   333→ */
   334→function filterSilencesByTakes(silences, takes) {
   335→  if (!takes || takes.length === 0) {
   336→    // No takes = all silences are safe to remove
   337→    return { safe: silences, protected: [] };
   338→  }
   339→
   340→  const safe = [];
   341→  const protected_ = [];
   342→
   343→  silences.forEach(silence => {
   344→    // Check if this silence overlaps with any take
   345→    const overlaps = takes.some(take => {
   346→      const takeStart = take.startTime || take.start;
   347→      const takeEnd = take.endTime || take.end;
   348→      // Overlap exists if silence starts before take ends AND silence ends after take starts
   349→      return silence.start < takeEnd && silence.end > takeStart;
   350→    });
   351→
   352→    if (overlaps) {
   353→      protected_.push(silence);
   354→    } else {
   355→      safe.push(silence);
   356→    }
   357→  });
   358→
   359→  console.log(`[SPLICE] Filtered silences: ${safe.length} safe, ${protected_.length} protected`);
   360→  return { safe, protected: protected_ };
   361→}
   362→
   363→// ============================================================================
   364→// TIMELINE MARKERS (Visual indicators on Premiere Pro timeline)
   365→// ============================================================================
   366→/**
   367→ * Add colored markers to the timeline for silences and takes.
   368→ * - Red markers: silences safe to remove
   369→ * - Yellow markers: silences protected by takes
   370→ * - Blue markers: takes (speech segments)
   371→ *
   372→ * @param {Array} safeToRemove - Silences safe to remove
   373→ * @param {Array} protectedSilences - Silences overlapping speech
   374→ * @param {Array} takes - Detected speech segments
   375→ * @returns {Promise<{added: number, errors: number}>}
   376→ */
   377→async function addTimelineMarkers(safeToRemove, protectedSilences, takes) {
   378→  const results = { added: 0, errors: 0 };
   379→
   380→  try {
   381→    const context = await getActiveSequence();
   382→    if (!context) {
   383→      console.warn('[SPLICE] No sequence available for markers');
   384→      return results;
   385→    }
   386→
   387→    const { sequence, project } = context;
   388→
   389→    // Get markers object for the sequence
   390→    const markers = await ppro.Markers.getMarkers(sequence);
   391→    if (!markers) {
   392→      console.warn('[SPLICE] Markers API not available');
   393→      return results;
   394→    }
   395→
   396→    // Collect all marker actions
   397→    const markerActions = [];
   398→
   399→    // Red markers for safe-to-remove silences
   400→    for (const silence of safeToRemove) {
   401→      try {
   402→        const duration = silence.end - silence.start;
   403→
   404→        // Use TickTime API directly (no TICKS_PER_SECOND needed)
   405→        const startTime = ppro.TickTime.createWithSeconds(silence.start);
   406→        const durationTime = ppro.TickTime.createWithSeconds(duration);
   407→
   408→        const action = markers.createAddMarkerAction(
   409→          `SPLICE: Remove (${duration.toFixed(2)}s)`,
   410→          ppro.Marker.MARKER_TYPE_COMMENT,
   411→          startTime,
   412→          durationTime,
   413→          `Silence to remove: ${formatTime(silence.start)} - ${formatTime(silence.end)}`
   414→        );
   415→        markerActions.push({ action, colorIndex: 1, type: 'silence' }); // Red = index 1
   416→      } catch (err) {
   417→        console.error('[SPLICE] Error creating silence marker:', err);
   418→        results.errors++;
   419→      }
   420→    }
   421→
   422→    // Yellow markers for protected silences
   423→    for (const silence of protectedSilences) {
   424→      try {
   425→        const duration = silence.end - silence.start;
   426→
   427→        // Use TickTime API directly (no TICKS_PER_SECOND needed)
   428→        const startTime = ppro.TickTime.createWithSeconds(silence.start);
   429→        const durationTime = ppro.TickTime.createWithSeconds(duration);
   430→
   431→        const action = markers.createAddMarkerAction(
   432→          `SPLICE: Protected (${duration.toFixed(2)}s)`,
   433→          ppro.Marker.MARKER_TYPE_COMMENT,
   434→          startTime,
   435→          durationTime,
   436→          `Protected silence (overlaps speech): ${formatTime(silence.start)} - ${formatTime(silence.end)}`
   437→        );
   438→        markerActions.push({ action, colorIndex: 4, type: 'protected' }); // Yellow = index 4
   439→      } catch (err) {
   440→        console.error('[SPLICE] Error creating protected marker:', err);
   441→        results.errors++;
   442→      }
   443→    }
   444→
   445→    // Blue markers for takes (speech segments)
   446→    for (const take of takes) {
   447→      try {
   448→        const start = take.startTime || take.start;
   449→        const end = take.endTime || take.end;
   450→        const duration = end - start;
   451→        const textPreview = take.text?.substring(0, 30) || 'Speech';
   452→
   453→        // Use TickTime API directly (no TICKS_PER_SECOND needed)
   454→        const startTime = ppro.TickTime.createWithSeconds(start);
   455→        const durationTime = ppro.TickTime.createWithSeconds(duration);
   456→
   457→        const action = markers.createAddMarkerAction(
   458→          `SPLICE: Take`,
   459→          ppro.Marker.MARKER_TYPE_COMMENT,
   460→          startTime,
   461→          durationTime,
   462→          `Speech segment: ${textPreview}${take.text?.length > 30 ? '...' : ''}`
   463→        );
   464→        markerActions.push({ action, colorIndex: 5, type: 'take' }); // Blue = index 5
   465→      } catch (err) {
   466→        console.error('[SPLICE] Error creating take marker:', err);
   467→        results.errors++;
   468→      }
   469→    }
   470→
   471→    // Execute all marker actions in a single transaction
   472→    if (markerActions.length > 0) {
   473→      await project.lockedAccess(async () => {
   474→        await project.executeTransaction((compoundAction) => {
   475→          for (const { action } of markerActions) {
   476→            compoundAction.addAction(action);
   477→          }
   478→        }, 'SPLICE: Add Timeline Markers');
   479→      });
   480→
   481→      results.added = markerActions.length;
   482→      console.log(`[SPLICE] Added ${results.added} timeline markers`);
   483→    }
   484→
   485→  } catch (err) {
   486→    console.error('[SPLICE] Timeline markers error:', err);
   487→    results.errors++;
   488→  }
   489→
   490→  return results;
   491→}
   492→
   493→/**
   494→ * Read current SPLICE marker positions from timeline.
   495→ * This allows users to manually adjust markers before applying.
   496→ * Returns silences based on current marker positions (not original detection).
   497→ */
   498→async function readMarkersAssilences() {
   499→  const silences = [];
   500→
   501→  try {
   502→    const context = await getActiveSequence();
   503→    if (!context) return silences;
   504→
   505→    const { sequence } = context;
   506→    const markers = await ppro.Markers.getMarkers(sequence);
   507→    if (!markers) return silences;
   508→
   509→    const allMarkers = await markers.getMarkers();
   510→    if (!allMarkers || allMarkers.length === 0) return silences;
   511→
   512→    for (const marker of allMarkers) {
   513→      const name = await marker.getName();
   514→
   515→      // Only read "Remove" markers (red ones that will be cut)
   516→      if (name && name.startsWith('SPLICE: Remove')) {
   517→        const startTickTime = await marker.getStart();
   518→        const durationTickTime = await marker.getDuration();
   519→
   520→        // Use TickTime.seconds property to get seconds directly (no TICKS_PER_SECOND needed)
   521→        const start = startTickTime.seconds;
   522→        const duration = durationTickTime.seconds;
   523→        const end = start + duration;
   524→
   525→        silences.push({
   526→          start,
   527→          end,
   528→          duration,
   529→          fromMarker: true  // Flag to indicate this came from marker adjustment
   530→        });
   531→      }
   532→    }
   533→
   534→    // Sort by start time
   535→    silences.sort((a, b) => a.start - b.start);
   536→    console.log(`[SPLICE] Read ${silences.length} silences from adjusted markers`);
   537→
   538→  } catch (err) {
   539→    console.error('[SPLICE] Error reading markers:', err);
   540→  }
   541→
   542→  return silences;
   543→}
   544→
   545→/**
   546→ * Remove all SPLICE markers from the timeline.
   547→ * Call this before adding new markers to avoid duplicates.
   548→ */
   549→async function clearSpliceMarkers() {
   550→  try {
   551→    const context = await getActiveSequence();
   552→    if (!context) return 0;
   553→
   554→    const { sequence, project } = context;
   555→    const markers = await ppro.Markers.getMarkers(sequence);
   556→    if (!markers) return 0;
   557→
   558→    // Get all markers
   559→    const allMarkers = await markers.getMarkers();
   560→    if (!allMarkers || allMarkers.length === 0) return 0;
   561→
   562→    // Filter SPLICE markers
   563→    const spliceMarkers = [];
   564→    for (const marker of allMarkers) {
   565→      const name = await marker.getName();
   566→      if (name && name.startsWith('SPLICE:')) {
   567→        spliceMarkers.push(marker);
   568→      }
   569→    }
   570→
   571→    if (spliceMarkers.length === 0) return 0;
   572→
   573→    // Remove all SPLICE markers in a single transaction
   574→    await project.lockedAccess(async () => {
   575→      await project.executeTransaction((compoundAction) => {
   576→        for (const marker of spliceMarkers) {
   577→          const removeAction = markers.createRemoveMarkerAction(marker);
   578→          compoundAction.addAction(removeAction);
   579→        }
   580→      }, 'SPLICE: Clear Markers');
   581→    });
   582→
   583→    console.log(`[SPLICE] Cleared ${spliceMarkers.length} previous markers`);
   584→    return spliceMarkers.length;
   585→
   586→  } catch (err) {
   587→    console.error('[SPLICE] Error clearing markers:', err);
   588→    return 0;
   589→  }
   590→}
