   882→async function applySelectedSilences() {
   883→  if (selectedSilenceIndices.size === 0) {
   884→    setStatus('No silences selected');
   885→    return;
   886→  }
   887→
   888→  showProgress('Reading marker positions...');
   889→
   890→  // Read current marker positions from timeline
   891→  // This allows users to manually adjust markers before applying
   892→  const markerSilences = await readMarkersAssilences();
   893→
   894→  let silencesToApply;
   895→  let adjustedCount = 0;
   896→
   897→  if (markerSilences.length > 0) {
   898→    // Use marker positions (may have been adjusted by user)
   899→    silencesToApply = markerSilences;
   900→    adjustedCount = markerSilences.length;
   901→    console.log(`[SPLICE] Using ${adjustedCount} silences from adjusted markers`);
   902→  } else {
   903→    // Fallback to original detection data
   904→    silencesToApply = safeSilences.filter((_, i) => selectedSilenceIndices.has(i));
   905→    console.log(`[SPLICE] Using ${silencesToApply.length} silences from original detection`);
   906→  }
   907→
   908→  if (silencesToApply.length === 0) {
   909→    setStatus('No silences to apply');
   910→    return;
   911→  }
   912→
   913→  // Update global silences for the apply function
   914→  setCurrentSilences(silencesToApply);
   915→
   916→  showProgress('Applying changes...');
   917→
   918→  // Apply to timeline
   919→  const result = await removeSilencesFromTimeline();
   920→
   921→  // Clear markers after applying
   922→  await clearSpliceMarkers();
   923→
   924→  // Hide preview
   925→  if (ui.combinedPreview) ui.combinedPreview.style.display = 'none';
   926→
   927→  // Show results
   928→  showSilenceResults({
   929→    count: silencesToApply.length,
   930→    timeSaved: silencesToApply.reduce((sum, s) => sum + (s.end - s.start), 0),
   931→    clipsModified: result
   932→  });
   933→
   934→  // Show advanced section
   935→  if (ui.advancedSection) ui.advancedSection.classList.remove('hidden');
   936→
   937→  const adjustedInfo = adjustedCount > 0 ? ' (from adjusted markers)' : '';
   938→  setStatus(`Removed ${silencesToApply.length} silence(s) - ${result} clip(s) modified${adjustedInfo}`);
   939→
   940→  // Refresh credits (await to prevent race conditions)
   941→  try {
   942→    await refreshCredits();
   943→  } catch (err) {
   944→    console.warn('[SPLICE] Credits refresh failed:', err);
   945→  }
   946→
   947→  // Clear preview state
   948→  previewSilences = [];
   949→  safeSilences = [];
   950→  protectedSilences = [];
   951→  previewTakes = [];
   952→  selectedSilenceIndices.clear();
   953→}
