  1093→/**
  1094→ * Find the first media item in the project bin (recursively)
  1095→ * @param {Object} item - ProjectItem (usually rootItem)
  1096→ * @returns {Promise<Object|null>} First media item found or null
  1097→ */
  1098→async function findFirstMediaItem(item) {
  1099→  if (!item) return null;
  1100→
  1101→  // 1. Check if this item is a Clip (Type 1)
  1102→  // Constants: 1=CLIP, 2=BIN, 3=ROOT, 4=FILE
  1103→  if (item.type === ppro.Constants.ProjectItemType.CLIP) {
  1104→    return item;
  1105→  }
  1106→
  1107→  // 2. If it's a Bin (Type 2) or Root (Type 3), search children
  1108→  if (item.children && item.children.numItems > 0) {
  1109→    for (let i = 0; i < item.children.numItems; i++) {
  1110→      const child = item.children[i];
  1111→      const found = await findFirstMediaItem(child);
  1112→      if (found) return found;
  1113→    }
  1114→  }
  1115→
  1116→  return null;
  1117→}
