   391→function deleteClipsAtSilencePointsInTrack(silences, trackType, trackIndex) {
   392→    try {
   393→        silences = JSON.parse(silences);
   394→    } catch (e) {
   395→        return JSON.stringify({ error: "Invalid JSON input: " + e.message });
   396→    }
   397→    var clips;
   398→    if (trackType == "video") {
   399→        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
   400→    } else if (trackType == "audio") {
   401→        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
   402→    }
   403→
   404→    var deleted = 0;
   405→    for (var i = clips.length - 1; i >= 0; i--) {
   406→        var clip = clips[i];
   407→        var start = clip.start.seconds;
   408→        var end = clip.end.seconds;
   409→        var center = (start + end) / 2;
   410→
   411→        for (var j = 0; j < silences.length; j++) {
   412→            var silence = silences[j];
   413→            var silenceCenter = (silence[0] + silence[1]) / 2;
   414→            if (silenceCenter > start && silenceCenter < end) {
   415→                clip.remove(false, false);
   416→                deleted++;
   417→                break;
   418→            }
   419→        }
   420→    }
   421→    return JSON.stringify({ deleted: deleted });
   422→}
