   424→function makeRemainingClipsContiguousInTrack(silences, deltas, trackType, trackIndex) {
   425→    try {
   426→        silences = JSON.parse(silences);
   427→    } catch (e) {
   428→        return JSON.stringify({ error: "Invalid JSON input for silences: " + e.message });
   429→    }
   430→    try {
   431→        deltas = JSON.parse(deltas);
   432→    } catch (e) {
   433→        return JSON.stringify({ error: "Invalid JSON input for deltas: " + e.message });
   434→    }
   435→
   436→    var clips;
   437→    if (trackType == "video") {
   438→        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
   439→    } else if (trackType == "audio") {
   440→        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
   441→    }
   442→
   443→    for (var i = 0; i < clips.length; i++) {
   444→        var clip = clips[i];
   445→        var end = clip.end.seconds;
   446→        var num_silences = -1;
   447→
   448→        for (var j = 0; j < silences.length; j++) {
   449→            if (end > silences[j][1]) {
   450→                num_silences++;
   451→            }
   452→        }
   453→
   454→        if (num_silences > -1 && num_silences < deltas.length) {
   455→            var delta = deltas[num_silences];
   456→            var t_start = clip.start;
   457→            t_start.seconds = t_start.seconds - delta;
   458→            clip.start = t_start;
   459→            var t_end = clip.end;
   460→            t_end.seconds = t_end.seconds - delta;
   461→            clip.end = t_end;
   462→        }
   463→    }
   464→    return JSON.stringify({ success: true });
   465→}
