   271→                }
   272→            }, timeout);
   273→
   274→            this.cs.evalScript(script, (result) => {
   275→                // Ignore late callbacks after timeout
   276→                if (completed) {
   277→                    console.warn('[JSX] Late evalScript result received after timeout - ignoring');
   278→                    return;
   279→                }
   280→
   281→                completed = true;
   282→                clearTimeout(timeoutId);
   283→
   284→                if (result === 'undefined' || result === undefined) {
   285→                    resolve(null);
   286→                    return;
   287→                }
   288→
   289→                // Check for EvalScript error or ExtendScript runtime errors
   290→                if (typeof result === 'string' && (
   291→                    result.indexOf('EvalScript error') !== -1 ||
   292→                    result.indexOf('Error:') !== -1 ||
   293→                    result.indexOf('TypeError:') !== -1 ||
   294→                    result.indexOf('ReferenceError:') !== -1
   295→                )) {
   296→                    reject(new Error(result));
   297→                    return;
   298→                }
   299→
   300→                // Try to parse JSON
   301→                try {
   302→                    const parsed = JSON.parse(result);
   303→                    if (parsed.error) {
   304→                        reject(new Error(parsed.error));
   305→                    } else {
   306→                        resolve(parsed);
   307→                    }
   308→                } catch (e) {
   309→                    // Return as-is if not JSON
   310→                    resolve(result);
   311→                }
   312→            });
   313→        });
   314→    },
   315→
   316→    /**
   317→     * Call a JSX function with arguments
   318→     * @param {string} funcName - Function name
   319→     * @param {...any} args - Arguments to pass (last arg can be {timeout: number})
   320→     * @returns {Promise<any>}
   321→     */
   322→    call: async function (funcName, ...args) {
   323→        await this.ensureInitialized();
   324→
   325→        // Check if last argument is an options object with timeout
   326→        let timeout = JSX_TIMEOUTS.DEFAULT;
   327→        let funcArgs = args;
   328→
   329→        if (args.length > 0 && typeof args[args.length - 1] === 'object' && args[args.length - 1] !== null) {
   330→            const lastArg = args[args.length - 1];
   331→            if ('_jsxTimeout' in lastArg) {
   332→                timeout = lastArg._jsxTimeout;
   333→                funcArgs = args.slice(0, -1);
   334→            }
   335→        }
   336→
   337→        const escapedArgs = funcArgs.map(arg => {
   338→            if (arg === null || arg === undefined) return 'null';
   339→            if (typeof arg === 'string') {
   340→                // Escape backslashes first, then other special chars
   341→                return `'${arg
   342→                    .replace(/\\/g, '\\\\')
   343→                    .replace(/'/g, "\\'")
   344→                    .replace(/\r/g, '\\r')
   345→                    .replace(/\n/g, '\\n')
   346→                    .replace(/\t/g, '\\t')}'`;
   347→            }
   348→            if (typeof arg === 'object') {
   349→                return `'${JSON.stringify(arg)
   350→                    .replace(/\\/g, '\\\\')
   351→                    .replace(/'/g, "\\'")}'`;
   352→            }
   353→            return String(arg);
   354→        });
   355→
   356→        const script = `${funcName}(${escapedArgs.join(', ')})`;
   357→        return this.evalScript(script, timeout);
   358→    },
   359→
   360→    /**
   361→     * Call a JSX function with explicit timeout
   362→     * Convenience wrapper for operations that need non-default timeouts
   363→     * @param {string} funcName - Function name
   364→     * @param {Array} args - Arguments array
   365→     * @param {number} timeout - Timeout in ms
   366→     * @returns {Promise<any>}
   367→     */
   368→    callWithTimeout: async function (funcName, args = [], timeout = JSX_TIMEOUTS.DEFAULT) {
   369→        return this.call(funcName, ...args, { _jsxTimeout: timeout });
   370→    }
   371→};
