   377→/**
   378→ * Fetch with timeout support
   379→ * @param {string} url - URL to fetch
   380→ * @param {object} options - Fetch options
   381→ * @param {number} timeout - Timeout in ms
   382→ */
   383→async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
   384→    // DIAGNOSTIC: Log fetch request details (debug mode only)
   385→    debugFetch('=== FETCH REQUEST START ===');
   386→    debugFetch('URL:', url);
   387→    debugFetch('Method:', options?.method || 'GET');
   388→    debugFetch('Headers:', options?.headers);
   389→    debugFetch('Timeout:', timeout);
   390→    debugFetch('Is CEP environment:', typeof CSInterface !== 'undefined');
   391→    debugFetch('=================================');
   392→
   393→    const controller = new AbortController();
   394→    const timeoutId = setTimeout(() => controller.abort(), timeout);
   395→
   396→    try {
   397→        const response = await fetch(url, {
   398→            ...options,
   399→            signal: controller.signal
   400→        });
   401→        clearTimeout(timeoutId);
   402→
   403→        // DIAGNOSTIC: Log successful response (debug mode only)
   404→        debugFetch('=== FETCH RESPONSE SUCCESS ===');
   405→        debugFetch('Status:', response.status);
   406→        debugFetch('OK:', response.ok);
   407→        debugFetch('=================================');
   408→
   409→        return response;
   410→    } catch (error) {
   411→        clearTimeout(timeoutId);
   412→
   413→        // DIAGNOSTIC: Log raw fetch error (debug mode only)
   414→        debugFetch('=== FETCH ERROR CAUGHT ===');
   415→        debugFetch('Error name:', error?.name);
   416→        debugFetch('Error message:', error?.message);
   417→        debugFetch('Error type:', typeof error);
   418→        debugFetch('Error constructor:', error?.constructor?.name);
   419→        debugFetch('Is AbortError:', error.name === 'AbortError');
   420→        debugFetch('Is TypeError:', error.name === 'TypeError');
   421→        debugFetch('Full error object:', JSON.stringify(error, Object.getOwnPropertyNames(error)));
   422→        debugFetch('=================================');
   423→
   424→        if (error.name === 'AbortError') {
   425→            throw new Error(`Request timeout after ${timeout}ms`);
   426→        }
   427→        throw error;
   428→    }
   429→}
