   468→/**
   469→ * Check if the access token is expired or about to expire
   470→ * @returns {boolean} True if token needs refresh
   471→ */
   472→function isTokenExpired() {
   473→    const settings = getSettings();
   474→    if (!settings.tokenExpiry) return true;
   475→
   476→    // Refresh if less than 5 minutes remaining
   477→    const bufferMs = 5 * 60 * 1000;
   478→    return Date.now() >= (settings.tokenExpiry - bufferMs);
   479→}
   480→
   481→/**
   482→ * Refresh the access token using the refresh token
   483→ * @returns {Promise<boolean>} True if refresh succeeded
   484→ */
   485→async function refreshAccessToken() {
   486→    const settings = getSettings();
   487→
   488→    if (!settings.refreshToken) {
   489→        debugLog('[Auth] No refresh token available');
   490→        return false;
   491→    }
   492→
   493→    try {
   494→        debugLog('[Auth] Refreshing access token...');
   495→
   496→        const response = await fetchWithTimeout(`${getBackendUrl()}/auth/refresh`, {
   497→            method: 'POST',
   498→            headers: { 'Content-Type': 'application/json' },
   499→            body: JSON.stringify({ refreshToken: settings.refreshToken })
   500→        }, 30000);
   501→
   502→        if (!response.ok) {
   503→            const errorMsg = await parseErrorResponse(response);
   504→            console.error('[Auth] Token refresh failed:', errorMsg);
   505→
   506→            // If refresh token is invalid, clear auth and require re-login
   507→            if (response.status === 401) {
   508→                clearAuthTokens();
   509→            }
   510→            return false;
   511→        }
   512→
   513→        const data = await response.json();
   514→
   515→        // Save new access token
   516→        const expiresInMs = (data.expiresIn || 86400) * 1000; // Default 24h
   517→        saveSettings({
   518→            accessToken: data.token,
   519→            tokenExpiry: Date.now() + expiresInMs
   520→        });
   521→
   522→        debugLog('[Auth] Access token refreshed successfully');
   523→        return true;
   524→    } catch (err) {
   525→        console.error('[Auth] Token refresh error:', err);
   526→        return false;
   527→    }
   528→}
   529→
   530→/**
   531→ * Clear all auth tokens (call on logout or auth failure)
   532→ */
   533→function clearAuthTokens() {
   534→    saveSettings({
   535→        accessToken: null,
   536→        refreshToken: null,
   537→        tokenExpiry: null
   538→    });
   539→    debugLog('[Auth] Auth tokens cleared');
   540→}
   541→
   542→/**
   543→ * Ensure we have a valid access token, refreshing if needed
   544→ * @returns {Promise<boolean>} True if we have a valid token
   545→ */
   546→async function ensureValidToken() {
   547→    const settings = getSettings();
   548→
   549→    // No token at all - need to login
   550→    if (!settings.accessToken) {
   551→        return false;
   552→    }
   553→
   554→    // Token not expired - good to go
   555→    if (!isTokenExpired()) {
   556→        return true;
   557→    }
   558→
   559→    // Try to refresh
   560→    return await refreshAccessToken();
   561→}
