   601→const SENSITIVE_FIELDS = ['customerId', 'licenseKey', 'accessToken', 'refreshToken'];
   602→
   603→// ============================================================================
   604→// ENCRYPTION HELPERS FOR SENSITIVE DATA (CEP-CRIT-002 FIX)
   605→// Uses AES-GCM encryption via Web Crypto API for proper security
   606→// ============================================================================
   607→
   608→// Encryption constants
   609→const SPLICE_ENCRYPTION = {
   610→    ALGORITHM: 'AES-GCM',
   611→    KEY_LENGTH: 256,
   612→    IV_LENGTH: 12,
   613→    TAG_LENGTH: 128,
   614→    SALT: 'SPLICE_PANEL_SALT_v1', // Static salt for key derivation
   615→    ITERATIONS: 100000,
   616→    PREFIX: 'ENC:v1:'  // Prefix to identify encrypted values
   617→};
   618→
   619→// Cached encryption key (derived once per session)
   620→let _encryptionKey = null;
   621→
   622→/**
   623→ * Get or derive the encryption key from a machine-specific identifier
   624→ * Uses PBKDF2 to derive a strong key from available identifiers
   625→ * @returns {Promise<CryptoKey>} The derived encryption key
   626→ */
   627→async function getEncryptionKey() {
   628→    if (_encryptionKey) {
   629→        return _encryptionKey;
   630→    }
   631→
   632→    try {
   633→        // Use a combination of identifiers for key derivation
   634→        // In CEP, we use localStorage ID + app version as the key material
   635→        let keyMaterial = localStorage.getItem('splice_device_id');
   636→        if (!keyMaterial) {
   637→            // Generate a random device ID on first use
   638→            keyMaterial = crypto.randomUUID ? crypto.randomUUID() :
   639→                'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
   640→                    const r = Math.random() * 16 | 0;
   641→                    const v = c === 'x' ? r : (r & 0x3 | 0x8);
   642→                    return v.toString(16);
   643→                });
   644→            localStorage.setItem('splice_device_id', keyMaterial);
   645→        }
   646→
   647→        const encoder = new TextEncoder();
   648→        const keyData = encoder.encode(keyMaterial + SPLICE_ENCRYPTION.SALT);
   649→
   650→        // Import the key material for PBKDF2
   651→        const baseKey = await crypto.subtle.importKey(
   652→            'raw',
   653→            keyData,
   654→            'PBKDF2',
   655→            false,
   656→            ['deriveKey']
   657→        );
   658→
   659→        // Derive the actual AES key
   660→        _encryptionKey = await crypto.subtle.deriveKey(
   661→            {
   662→                name: 'PBKDF2',
   663→                salt: encoder.encode(SPLICE_ENCRYPTION.SALT),
   664→                iterations: SPLICE_ENCRYPTION.ITERATIONS,
   665→                hash: 'SHA-256'
   666→            },
   667→            baseKey,
   668→            {
   669→                name: SPLICE_ENCRYPTION.ALGORITHM,
   670→                length: SPLICE_ENCRYPTION.KEY_LENGTH
   671→            },
   672→            false,
   673→            ['encrypt', 'decrypt']
   674→        );
   675→
   676→        return _encryptionKey;
   677→    } catch (e) {
   678→        debugLog('[Config] Key derivation error:', e);
   679→        return null;
   680→    }
   681→}
   682→
   683→/**
   684→ * Encrypt data for secure localStorage storage using AES-GCM
   685→ *
   686→ * @param {any} data - Data to encrypt
   687→ * @returns {Promise<string|null>} Encrypted string with prefix or null on error
   688→ */
   689→async function encryptForStorageAsync(data) {
   690→    try {
   691→        if (data === null || data === undefined) {
   692→            return null;
   693→        }
   694→
   695→        const key = await getEncryptionKey();
   696→        if (!key) {
   697→            // Fallback to legacy encoding if crypto not available
   698→            debugLog('[Config] Crypto not available, using legacy encoding');
   699→            return legacyEncode(data);
   700→        }
   701→
   702→        const encoder = new TextEncoder();
   703→        const jsonStr = JSON.stringify(data);
   704→        const plaintext = encoder.encode(jsonStr);
   705→
   706→        // Generate random IV
   707→        const iv = crypto.getRandomValues(new Uint8Array(SPLICE_ENCRYPTION.IV_LENGTH));
   708→
   709→        // Encrypt
   710→        const ciphertext = await crypto.subtle.encrypt(
   711→            {
   712→                name: SPLICE_ENCRYPTION.ALGORITHM,
   713→                iv: iv,
   714→                tagLength: SPLICE_ENCRYPTION.TAG_LENGTH
   715→            },
   716→            key,
   717→            plaintext
   718→        );
   719→
   720→        // Combine IV + ciphertext and encode as base64
   721→        const combined = new Uint8Array(iv.length + ciphertext.byteLength);
   722→        combined.set(iv);
   723→        combined.set(new Uint8Array(ciphertext), iv.length);
   724→
   725→        // Convert to base64 with prefix
   726→        const base64 = btoa(String.fromCharCode(...combined));
   727→        return SPLICE_ENCRYPTION.PREFIX + base64;
   728→    } catch (e) {
   729→        debugLog('[Config] Encryption error:', e);
   730→        return legacyEncode(data);
   731→    }
   732→}
   733→
   734→/**
   735→ * Decrypt data from localStorage using AES-GCM
   736→ *
   737→ * @param {string} data - Encrypted string
   738→ * @returns {Promise<any|null>} Decrypted data or null on error
   739→ */
   740→async function decryptFromStorageAsync(data) {
   741→    try {
   742→        if (!data || typeof data !== 'string') {
   743→            return null;
   744→        }
   745→
   746→        // Check if this is the new encrypted format
   747→        if (!data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   748→            // Try legacy decoding for backward compatibility
   749→            return legacyDecode(data);
   750→        }
   751→
   752→        const key = await getEncryptionKey();
   753→        if (!key) {
   754→            debugLog('[Config] Crypto not available for decryption');
   755→            return null;
   756→        }
   757→
   758→        // Remove prefix and decode base64
   759→        const base64Data = data.slice(SPLICE_ENCRYPTION.PREFIX.length);
   760→        const combined = Uint8Array.from(atob(base64Data), c => c.charCodeAt(0));
   761→
   762→        // Extract IV and ciphertext
   763→        const iv = combined.slice(0, SPLICE_ENCRYPTION.IV_LENGTH);
   764→        const ciphertext = combined.slice(SPLICE_ENCRYPTION.IV_LENGTH);
   765→
   766→        // Decrypt
   767→        const plaintext = await crypto.subtle.decrypt(
   768→            {
   769→                name: SPLICE_ENCRYPTION.ALGORITHM,
   770→                iv: iv,
   771→                tagLength: SPLICE_ENCRYPTION.TAG_LENGTH
   772→            },
   773→            key,
   774→            ciphertext
   775→        );
   776→
   777→        const decoder = new TextDecoder();
   778→        const jsonStr = decoder.decode(plaintext);
   779→        return JSON.parse(jsonStr);
   780→    } catch (e) {
   781→        // Try legacy decoding as fallback
   782→        return legacyDecode(data);
   783→    }
   784→}
   785→
   786→/**
   787→ * Legacy encoding (Base64) for backward compatibility
   788→ * @param {any} data - Data to encode
   789→ * @returns {string|null} Encoded string
   790→ */
   791→function legacyEncode(data) {
   792→    try {
   793→        if (data === null || data === undefined) {
   794→            return null;
   795→        }
   796→        const jsonStr = JSON.stringify(data);
   797→        const encoded = encodeURIComponent(jsonStr);
   798→        return btoa(encoded);
   799→    } catch (e) {
   800→        return null;
   801→    }
   802→}
   803→
   804→/**
   805→ * Legacy decoding (Base64) for backward compatibility
   806→ * @param {string} data - Encoded string
   807→ * @returns {any|null} Decoded data
   808→ */
   809→function legacyDecode(data) {
   810→    try {
   811→        if (!data || typeof data !== 'string') {
   812→            return null;
   813→        }
   814→        // Skip if it has the encryption prefix
   815→        if (data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   816→            return null;
   817→        }
   818→        const decoded = atob(data);
   819→        const jsonStr = decodeURIComponent(decoded);
   820→        return JSON.parse(jsonStr);
   821→    } catch (e) {
   822→        return null;
   823→    }
   824→}
   825→
   826→/**
   827→ * Synchronous wrapper for encryption (starts async operation)
   828→ * For backward compatibility with existing sync code
   829→ * @param {any} data - Data to encode
   830→ * @returns {string|null} Encoded string (legacy format until async completes)
   831→ */
   832→function encryptForStorage(data) {
   833→    // Return legacy encoding synchronously
   834→    // The async version should be preferred for new code
   835→    return legacyEncode(data);
   836→}
   837→
   838→/**
   839→ * Synchronous wrapper for decryption
   840→ * For backward compatibility with existing sync code
   841→ * @param {string} data - Encoded string
   842→ * @returns {any|null} Decoded data
   843→ */
   844→function decryptFromStorage(data) {
   845→    // Handle new encrypted format by returning null (needs async)
   846→    if (data && data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   847→        debugLog('[Config] Encrypted data requires async decryption');
   848→        return null;
   849→    }
   850→    return legacyDecode(data);
   851→}
   852→
   853→/**
   854→ * Check if a value appears to be encrypted
   855→ *
   856→ * @param {any} value - Value to check
   857→ * @returns {boolean} True if value appears to be encrypted
   858→ */
   859→function isEncryptedValue(value) {
   860→    if (!value || typeof value !== 'string') {
   861→        return false;
   862→    }
   863→    // New encryption format
   864→    if (value.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   865→        return true;
   866→    }
   867→    // Unencrypted values typically start with known patterns
   868→    if (value.startsWith('cus_') || value.startsWith('SPLICE-')) {
   869→        return false;
   870→    }
   871→    // Check if it's legacy base64 encoded JSON
   872→    try {
   873→        const decoded = atob(value);
   874→        const jsonStr = decodeURIComponent(decoded);
   875→        JSON.parse(jsonStr);
   876→        return true;
   877→    } catch {
   878→        return false;
   879→    }
   880→}
   881→
   882→/**
   883→ * Encrypt sensitive fields in settings object
   884→ *
   885→ * @param {Object} settings - Settings object with plaintext sensitive fields
   886→ * @returns {Object} Settings with encrypted sensitive fields
   887→ */
   888→function encryptSensitiveFields(settings) {
   889→    const encrypted = { ...settings };
   890→
   891→    for (const field of SENSITIVE_FIELDS) {
   892→        if (encrypted[field] !== null && encrypted[field] !== undefined) {
   893→            // Only encrypt if not already encrypted
   894→            if (!isEncryptedValue(encrypted[field])) {
   895→                encrypted[field] = encryptForStorage(encrypted[field]);
   896→                debugLog(`[Config] Encrypted field: ${field}`);
   897→            }
   898→        }
   899→    }
   900→
   901→    return encrypted;
   902→}
   903→
   904→/**
   905→ * Decrypt sensitive fields in settings object
   906→ *
   907→ * @param {Object} settings - Settings object with encrypted sensitive fields
   908→ * @returns {Object} Settings with decrypted sensitive fields
   909→ */
   910→function decryptSensitiveFields(settings) {
   911→    const decrypted = { ...settings };
   912→
   913→    for (const field of SENSITIVE_FIELDS) {
   914→        if (decrypted[field] !== null && decrypted[field] !== undefined) {
   915→            // Check if it looks encrypted before attempting to decrypt
   916→            if (isEncryptedValue(decrypted[field])) {
   917→                const decryptedValue = decryptFromStorage(decrypted[field]);
   918→                if (decryptedValue !== null) {
   919→                    decrypted[field] = decryptedValue;
   920→                    debugLog(`[Config] Decrypted field: ${field}`);
   921→                }
   922→            }
   923→            // If not encrypted (legacy data), keep the original value
   924→        }
   925→    }
   926→
   927→    return decrypted;
   928→}
   929→
   930→/**
   931→ * Internal save function to avoid recursion during migration
   932→ * @private
   933→ */
   934→function _saveSettingsInternal(settings) {
   935→    try {
   936→        // Encrypt sensitive fields before storing
   937→        const toStore = encryptSensitiveFields(settings);
   938→
   939→        // Add version marker for future migrations
   940→        toStore._storageVersion = SETTINGS_STORAGE_VERSION;
   941→
   942→        localStorage.setItem('spliceSettings', JSON.stringify(toStore));
   943→        return true;
   944→    } catch (e) {
   945→        console.warn('[Config] Error saving settings:', e);
   946→        return false;
   947→    }
   948→}
   949→
   950→/**
   951→ * Get settings from localStorage
   952→ * Handles migration from unencrypted to encrypted storage format
   953→ */
   954→function getSettings() {
   955→    try {
   956→        const stored = localStorage.getItem('spliceSettings');
   957→        if (stored) {
   958→            const parsed = JSON.parse(stored);
   959→
   960→            // Decrypt sensitive fields (handles both encrypted and legacy unencrypted data)
   961→            const decrypted = decryptSensitiveFields(parsed);
   962→
   963→            // Check if migration is needed (no version marker = legacy data)
   964→            if (!parsed._storageVersion) {
   965→                // Migrate legacy data: save with encryption and version marker
   966→                debugLog('[Config] Migrating legacy settings to encrypted format');
   967→                const migratedSettings = { ...DEFAULT_SETTINGS, ...decrypted };
   968→                // Use internal save to avoid infinite recursion
   969→                _saveSettingsInternal(migratedSettings);
   970→            }
   971→
   972→            // Return merged settings without internal fields
   973→            const result = { ...DEFAULT_SETTINGS, ...decrypted };
   974→            delete result._storageVersion;
   975→            return result;
   976→        }
   977→    } catch (e) {
   978→        console.warn('[Config] Error loading settings:', e);
   979→    }
   980→    return { ...DEFAULT_SETTINGS };
   981→}
   982→
   983→/**
   984→ * Save settings to localStorage
   985→ * Encrypts sensitive fields before storing
   986→ */
   987→function saveSettings(settings) {
   988→    try {
   989→        // Get current settings (decrypted) and merge with new settings
   990→        const current = getSettings();
   991→        const merged = { ...current, ...settings };
   992→
   993→        return _saveSettingsInternal(merged);
   994→    } catch (e) {
   995→        console.warn('[Config] Error saving settings:', e);
   996→        return false;
   997→    }
   998→}
   999→
  1000→/**
  1001→ * Clear specific setting
  1002→ */
  1003→function clearSetting(key) {
  1004→    const settings = getSettings();
  1005→    delete settings[key];
  1006→    _saveSettingsInternal(settings);
  1007→}
