  1081→/**
  1082→ * SECURITY: Escape HTML to prevent XSS attacks
  1083→ * Use this when inserting untrusted content into innerHTML
  1084→ * @param {string} str - String to escape
  1085→ * @returns {string} Escaped string safe for innerHTML
  1086→ */
  1087→function escapeHtml(str) {
  1088→    if (str === null || str === undefined) return '';
  1089→    if (typeof str !== 'string') str = String(str);
  1090→
  1091→    const escapeMap = {
  1092→        '&': '&amp;',
  1093→        '<': '&lt;',
  1094→        '>': '&gt;',
  1095→        '"': '&quot;',
  1096→        "'": '&#x27;',
  1097→        '/': '&#x2F;',
  1098→        '`': '&#x60;'
  1099→    };
  1100→
  1101→    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
  1102→}
  1103→
  1104→/**
  1105→ * SECURITY: Create safe HTML from template with escaped values
  1106→ * @param {TemplateStringsArray} strings - Template strings
  1107→ * @param {...any} values - Values to escape
  1108→ * @returns {string} HTML with escaped values
  1109→ */
  1110→function safeHtml(strings, ...values) {
  1111→    return strings.reduce((result, str, i) => {
  1112→        const value = values[i - 1];
  1113→        const escaped = escapeHtml(value);
  1114→        return result + escaped + str;
  1115→    });
  1116→}
