  1118→/**
  1119→ * Format time in MM:SS or HH:MM:SS
  1120→ */
  1121→function formatTime(seconds) {
  1122→    if (isNaN(seconds) || seconds < 0) return '0:00';
  1123→
  1124→    const hours = Math.floor(seconds / 3600);
  1125→    const minutes = Math.floor((seconds % 3600) / 60);
  1126→    const secs = Math.floor(seconds % 60);
  1127→
  1128→    if (hours > 0) {
  1129→        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  1130→    }
  1131→    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  1132→}
