  1145→/**
  1146→ * Debounce function calls
  1147→ */
  1148→function debounce(func, wait) {
  1149→    let timeout;
  1150→    return function executedFunction(...args) {
  1151→        const later = () => {
  1152→            clearTimeout(timeout);
  1153→            func(...args);
  1154→        };
  1155→        clearTimeout(timeout);
  1156→        timeout = setTimeout(later, wait);
  1157→    };
  1158→}
