  1405→/**
  1406→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1407→ * Character set excludes confusing chars: 0/O, 1/I/L
  1408→ */
  1409→function isValidLicenseKeyFormat(key) {
  1410→    if (!key || typeof key !== 'string') return false;
  1411→    const normalized = key.toUpperCase().trim();
  1412→    const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
  1413→    return pattern.test(normalized);
  1414→}
  1415→
  1416→/**
  1417→ * Initialize login modal handlers
  1418→ */
  1419→function initLoginModal() {
  1420→    const loginModal = document.getElementById('loginModal');
  1421→    const closeLoginBtn = document.getElementById('closeLoginBtn');
  1422→    const saveLoginBtn = document.getElementById('saveLoginBtn');
  1423→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1424→    const creditBadge = document.getElementById('creditBadge');
  1425→
  1426→    // Handle credit badge click - retry on error, or show login modal
  1427→    if (creditBadge) {
  1428→        creditBadge.addEventListener('click', async () => {
  1429→            if (creditBadge.classList.contains('error')) {
  1430→                creditBadge.textContent = '...';
  1431→                if (typeof refreshCredits === 'function') {
  1432→                    await refreshCredits();
  1433→                }
  1434→                return;
  1435→            }
  1436→            showLoginModal();
  1437→        });
  1438→    }
  1439→
  1440→    // Close login modal
  1441→    if (closeLoginBtn && loginModal) {
  1442→        closeLoginBtn.addEventListener('click', () => {
  1443→            loginModal.classList.add('hidden');
  1444→        });
  1445→    }
  1446→
  1447→    // Close on backdrop click
  1448→    if (loginModal) {
  1449→        loginModal.addEventListener('click', (e) => {
  1450→            if (e.target === loginModal) {
  1451→                loginModal.classList.add('hidden');
  1452→            }
  1453→        });
  1454→    }
  1455→
  1456→    // Activate license key
  1457→    if (saveLoginBtn) {
  1458→        saveLoginBtn.addEventListener('click', async () => {
  1459→            const licenseKey = licenseKeyInput?.value?.trim()?.toUpperCase();
  1460→
  1461→            if (!licenseKey) {
  1462→                showLoginError('Please enter your license key');
  1463→                return;
  1464→            }
  1465→
  1466→            if (!isValidLicenseKeyFormat(licenseKey)) {
  1467→                showLoginError('Invalid format. Expected: SPLICE-XXXX-XXXX-XXXX');
  1468→                return;
  1469→            }
  1470→
  1471→            saveLoginBtn.disabled = true;
  1472→            saveLoginBtn.textContent = 'Activating...';
  1473→
  1474→            try {
  1475→                // Use /auth/login endpoint which returns JWT tokens
  1476→                const response = await fetchWithTimeout(`${getBackendUrl()}/auth/login`, {
  1477→                    method: 'POST',
  1478→                    headers: { 'Content-Type': 'application/json' },
  1479→                    body: JSON.stringify({ licenseKey: licenseKey })
  1480→                }, SPLICE_CONFIG.FETCH_TIMEOUT);
  1481→
  1482→                const result = await response.json();
  1483→
  1484→                if (!result.success) {
  1485→                    showLoginError(result.error || 'Activation failed');
  1486→                    return;
  1487→                }
  1488→
  1489→                // Store JWT tokens and customer info
  1490→                const expiresInMs = (result.expiresIn || 86400) * 1000; // Default 24h
  1491→                saveSettings({
  1492→                    customerId: result.customerId,
  1493→                    accessToken: result.token,
  1494→                    refreshToken: result.refreshToken,
  1495→                    tokenExpiry: Date.now() + expiresInMs,
  1496→                    licenseKey: licenseKey // Keep for re-authentication
  1497→                });
  1498→
  1499→                loginModal?.classList.add('hidden');
  1500→
  1501→                if (typeof refreshCredits === 'function') {
  1502→                    await refreshCredits();
  1503→                }
  1504→
  1505→                const tierName = result.tier ? result.tier.charAt(0).toUpperCase() + result.tier.slice(1) : 'Unknown';
  1506→                setStatus(`License activated! ${tierName} tier`);
  1507→            } catch (err) {
  1508→                console.error('[SPLICE] License activation error:', err);
  1509→
  1510→                // DIAGNOSTIC LOGGING: Capture exact error details (debug mode only)
  1511→                debugLog('=== CEP LICENSE ACTIVATION ERROR DIAGNOSTICS ===');
  1512→                debugLog('Error name:', err?.name);
  1513→                debugLog('Error message:', err?.message);
  1514→                debugLog('Error stack:', err?.stack);
  1515→                debugLog('Backend URL:', getBackendUrl());
  1516→                debugLog('Fetch timeout:', SPLICE_CONFIG.FETCH_TIMEOUT);
  1517→                debugLog('Is CEP environment:', typeof CSInterface !== 'undefined');
  1518→                debugLog('User agent:', navigator?.userAgent);
  1519→                debugLog('Error constructor:', err?.constructor?.name);
  1520→                debugLog('Is TypeError:', err?.name === 'TypeError');
  1521→                debugLog('Message contains "Failed to fetch":', err?.message?.includes('Failed to fetch'));
  1522→                debugLog('Message contains "fetch":', err?.message?.toLowerCase()?.includes('fetch'));
  1523→                debugLog('Message contains "network":', err?.message?.toLowerCase()?.includes('network'));
  1524→                debugLog('Message contains "CORS":', err?.message?.toLowerCase()?.includes('cors'));
  1525→                debugLog('Message contains "SSL":', err?.message?.toLowerCase()?.includes('ssl'));
  1526→                debugLog('Message contains "certificate":', err?.message?.toLowerCase()?.includes('certificate'));
  1527→                debugLog('Full error object:', JSON.stringify(err, Object.getOwnPropertyNames(err)));
  1528→                debugLog('================================================');
  1529→
  1530→                // Provide detailed error message based on error type
  1531→                let errorMessage = 'Connection error. ';
  1532→
  1533→                if (err.message === 'Request timed out') {
  1534→                    errorMessage += 'Server is not responding. Check if backend is running.';
  1535→                } else if (err.message?.includes('Failed to fetch') || err.name === 'TypeError') {
  1536→                    errorMessage += `Cannot reach ${getBackendUrl()}. Check network/firewall.`;
  1537→                } else if (err.message?.includes('CORS')) {
  1538→                    errorMessage += 'CORS error. Contact support.';
  1539→                } else if (err.message?.includes('SSL') || err.message?.includes('certificate')) {
  1540→                    errorMessage += 'SSL certificate error. Try updating your system certificates.';
  1541→                } else if (err.message) {
  1542→                    errorMessage += err.message;
  1543→                } else {
  1544→                    errorMessage += 'Unknown error. Check console for details.';
  1545→                }
  1546→
  1547→                debugLog('Final error message shown to user:', errorMessage);
  1548→                showLoginError(errorMessage);
  1549→            } finally {
  1550→                saveLoginBtn.disabled = false;
  1551→                saveLoginBtn.textContent = 'Activate';
  1552→            }
  1553→        });
  1554→    }
  1555→
  1556→    // Email lookup
  1557→    const lookupLicenseBtn = document.getElementById('lookupLicenseBtn');
  1558→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1559→
  1560→    if (lookupLicenseBtn) {
  1561→        lookupLicenseBtn.addEventListener('click', async () => {
  1562→            const email = lookupEmailInput?.value?.trim()?.toLowerCase();
  1563→
  1564→            if (!email) {
  1565→                showLoginError('Please enter your email address');
  1566→                return;
  1567→            }
  1568→
  1569→            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1570→            if (!emailRegex.test(email)) {
  1571→                showLoginError('Please enter a valid email address');
  1572→                return;
  1573→            }
  1574→
  1575→            lookupLicenseBtn.disabled = true;
  1576→            lookupLicenseBtn.textContent = 'Looking up...';
  1577→
  1578→            try {
