  1580→                    method: 'POST',
  1581→                    headers: { 'Content-Type': 'application/json' },
  1582→                    body: JSON.stringify({ email })
  1583→                }, 30000);
  1584→
  1585→                const result = await response.json();
  1586→
  1587→                // Note: For security, the backend does NOT return the license key directly.
  1588→                // It sends the key via email to prevent enumeration attacks.
  1589→                if (result.success) {
  1590→                    setStatus(result.message || 'If a license exists, it will be sent to your email.');
  1591→                } else {
  1592→                    showLoginError(result.error || 'Lookup failed. Please try again.');
  1593→                }
  1594→            } catch (err) {
  1595→                console.error('[SPLICE] License lookup error:', err);
  1596→                showLoginError('Failed to look up license. Please try again.');
  1597→            } finally {
  1598→                lookupLicenseBtn.disabled = false;
  1599→                lookupLicenseBtn.textContent = 'Lookup';
  1600→            }
  1601→        });
  1602→    }
