  1731→// ============================================================================
  1732→// CENTRALIZED ERROR LOGGING (FIX: CEP-ERR-007)
  1733→// Captures errors for debugging across different Mac configurations
  1734→// ============================================================================
  1735→
  1736→/**
  1737→ * Error log storage - persisted to localStorage for debugging
  1738→ * Stores the last 50 errors with timestamps and context
  1739→ */
  1740→const ERROR_LOG_KEY = 'spliceErrorLog';
  1741→const MAX_ERROR_LOG_SIZE = 50;
  1742→
  1743→/**
  1744→ * Log an error with context for debugging
  1745→ * FIX: CEP-ERR-007 - Centralized error capture for debugging crashes
  1746→ *
  1747→ * @param {string} module - Module name (e.g., 'JSX', 'Silence', 'Takes')
  1748→ * @param {string} operation - Operation that failed
  1749→ * @param {Error|string} error - Error object or message
  1750→ * @param {Object} context - Additional context for debugging
  1751→ */
  1752→function logError(module, operation, error, context = {}) {
  1753→    const errorEntry = {
  1754→        timestamp: new Date().toISOString(),
  1755→        module,
  1756→        operation,
  1757→        message: error?.message || String(error),
  1758→        stack: error?.stack || null,
  1759→        code: error?.code || null,
  1760→        context: {
  1761→            ...context,
  1762→            userAgent: navigator?.userAgent,
  1763→            isCEP: typeof window.__adobe_cep__ !== 'undefined',
  1764→            isOnline: navigator?.onLine
  1765→        }
  1766→    };
  1767→
  1768→    // Log to console
  1769→    console.error(`[SPLICE ${module}] ${operation}:`, error);
  1770→    if (SPLICE_DEBUG) {
  1771→        console.error('[SPLICE ERROR CONTEXT]', errorEntry);
  1772→    }
  1773→
  1774→    // Persist to localStorage for later debugging
  1775→    try {
  1776→        let errorLog = [];
  1777→        const stored = localStorage.getItem(ERROR_LOG_KEY);
  1778→        if (stored) {
  1779→            errorLog = JSON.parse(stored);
  1780→        }
  1781→
  1782→        // Add new error and trim to max size
  1783→        errorLog.unshift(errorEntry);
  1784→        if (errorLog.length > MAX_ERROR_LOG_SIZE) {
  1785→            errorLog = errorLog.slice(0, MAX_ERROR_LOG_SIZE);
  1786→        }
  1787→
  1788→        localStorage.setItem(ERROR_LOG_KEY, JSON.stringify(errorLog));
  1789→    } catch (e) {
  1790→        // Silent fail - localStorage might be full or unavailable
  1791→        console.warn('[SPLICE] Failed to persist error log:', e);
  1792→    }
  1793→
  1794→    return errorEntry;
  1795→}
  1796→
  1797→/**
  1798→ * Get the stored error log for debugging
  1799→ * @returns {Array} Array of error entries
  1800→ */
  1801→function getErrorLog() {
  1802→    try {
  1803→        const stored = localStorage.getItem(ERROR_LOG_KEY);
  1804→        return stored ? JSON.parse(stored) : [];
  1805→    } catch (e) {
  1806→        return [];
  1807→    }
  1808→}
  1809→
  1810→/**
  1811→ * Clear the error log
  1812→ */
  1813→function clearErrorLog() {
  1814→    try {
  1815→        localStorage.removeItem(ERROR_LOG_KEY);
  1816→    } catch (e) {
  1817→        // Silent fail
  1818→    }
  1819→}
  1820→
  1821→/**
  1822→ * Format error log for display or copy
  1823→ * @returns {string} Formatted error log text
  1824→ */
  1825→function formatErrorLog() {
  1826→    const log = getErrorLog();
  1827→    if (log.length === 0) {
  1828→        return 'No errors logged.';
  1829→    }
  1830→
  1831→    return log.map((entry, i) => {
  1832→        return [
  1833→            `--- Error ${i + 1} ---`,
  1834→            `Time: ${entry.timestamp}`,
  1835→            `Module: ${entry.module}`,
  1836→            `Operation: ${entry.operation}`,
  1837→            `Message: ${entry.message}`,
  1838→            entry.code ? `Code: ${entry.code}` : null,
  1839→            entry.stack ? `Stack: ${entry.stack}` : null,
  1840→            `CEP: ${entry.context?.isCEP ? 'Yes' : 'No'}`,
  1841→            `Online: ${entry.context?.isOnline ? 'Yes' : 'No'}`,
  1842→            ''
  1843→        ].filter(Boolean).join('\n');
  1844→    }).join('\n');
  1845→}
  1846→
  1847→// ============================================================================
  1848→// GLOBAL ERROR HANDLERS (FIX: CEP-ERR-008)
  1849→// Catch unhandled errors and promise rejections
  1850→// ============================================================================
  1851→
  1852→/**
  1853→ * Global error handler for uncaught exceptions
  1854→ */
  1855→window.addEventListener('error', (event) => {
