  1866→window.addEventListener('unhandledrejection', (event) => {
  1867→    logError('Global', 'Unhandled Promise Rejection', event.reason, {
  1868→        promise: 'rejected'
  1869→    });
  1870→});
  1871→
  1872→// Initialize JSX bridge when loaded
  1873→document.addEventListener('DOMContentLoaded', () => {
  1874→    jsx.init();
