  ui.jcutLeadInValue = document.getElementById('jcutLeadInValue');
  ui.jcutLeadOut = document.getElementById('jcutLeadOut');
  ui.jcutLeadOutValue = document.getElementById('jcutLeadOutValue');

  // Progress
