            const wasCollapsed = panel.classList.contains('collapsed');
            panel.classList.toggle('collapsed');
            const icon = toggle.querySelector('.toggle-icon');
            debugLog('wasCollapsed:', wasCollapsed, 'icon:', !!icon);
