     1→/**
     2→ * SPLICE CEP Host Script
     3→ * ExtendScript for Premiere Pro
     4→ *
     5→ * Provides all Premiere Pro API functions called via jsx.evalScript()
     6→ * Based on proven patterns from FireCut and SPLICE UXP builder
     7→ */
     8→
     9→// Include shared utilities (constants, time conversion)
    10→#include "jsxUtils.jsx"
    11→
    12→// ============================================================================
    13→// RUNTIME STATE (not in jsxUtils because they are mutable)
    14→// ============================================================================
    15→var temp_bin = null;
    16→var sequence_default_timecode = null;
    17→
    18→// ============================================================================
    19→// INITIALIZATION
    20→// ============================================================================
    21→
    22→/**
    23→ * Check if QE (Quality Engine) DOM is available
    24→ * QE provides extended functionality not available in standard DOM
    25→ */
    26→function isQEAvailable() {
    27→    try {
    28→        return typeof qe !== 'undefined' && qe !== null && qe.project;
    29→    } catch (e) {
    30→        return false;
    31→    }
    32→}
    33→
    34→/**
    35→ * Enable QE DOM if not already enabled
    36→ * @returns {boolean} true if QE is now available
    37→ */
    38→function enableQE() {
    39→    try {
    40→        if (isQEAvailable()) return true;
    41→        app.enableQE();
    42→        return isQEAvailable();
    43→    } catch (e) {
    44→        $.writeln('[SPLICE] enableQE failed: ' + e.message);
    45→        return false;
    46→    }
    47→}
    48→
    49→/**
    50→ * Safe wrapper for QE operations
    51→ * @param {Function} operation - The QE operation to execute
    52→ * @param {string} operationName - Name for error reporting
    53→ * @returns {Object} Result or error object
    54→ */
    55→function safeQEOperation(operation, operationName) {
    56→    try {
    57→        if (!isQEAvailable()) {
    58→            if (!enableQE()) {
    59→                return { error: "QE not available. Please ensure Premiere Pro supports QE DOM." };
    60→            }
    61→        }
    62→        return operation();
    63→    } catch (e) {
    64→        return { error: operationName + " failed: " + e.message };
    65→    }
    66→}
    67→
    68→function initialise() {
    69→    storeDefaultTimecode();
    70→    // Try to enable QE on startup
    71→    var qeStatus = enableQE();
    72→    return JSON.stringify({ success: true, version: SPLICE_VERSION, qeAvailable: qeStatus });
    73→}
    74→
    75→function storeDefaultTimecode() {
    76→    if (app.project.activeSequence) {
    77→        sequence_default_timecode = app.project.activeSequence.getSettings().videoDisplayFormat;
    78→    }
    79→}
    80→
    81→function checkSequenceOpen() {
    82→    try {
    83→        // Quick validation checks without logging every step
    84→        if (!app || !app.project || !app.project.sequences) {
    85→            $.writeln('[SPLICE] checkSequenceOpen: ERROR - Premiere Pro app/project not ready');
    86→            return false;
    87→        }
    88→
    89→        // Primary check: Standard DOM activeSequence
    90→        if (app.project.activeSequence) {
    91→            var seq = app.project.activeSequence;
    92→            if (seq.name && seq.sequenceID) {
    93→                $.writeln('[SPLICE] checkSequenceOpen: ✓ Active sequence found: ' + seq.name);
    94→                return true;
    95→            }
    96→        }
    97→
    98→        // Fallback: Try QE DOM if standard DOM failed
    99→        if (enableQE()) {
   100→            try {
   101→                var qeSeq = qe.project.getActiveSequence();
   102→                if (qeSeq && qeSeq.name) {
   103→                    $.writeln('[SPLICE] checkSequenceOpen: ✓ Active sequence found via QE: ' + qeSeq.name);
   104→                    return true;
   105→                }
   106→            } catch (qeErr) {
   107→                // Silent fallback failure - not critical
   108→            }
   109→        }
   110→
   111→        $.writeln('[SPLICE] checkSequenceOpen: ✗ No active sequence found');
   112→        return false;
   113→    } catch (e) {
   114→        $.writeln('[SPLICE] checkSequenceOpen: ERROR - ' + e.message);
   115→        return false;
   116→    }
   117→}
   118→
   119→function getVersion() {
   120→    return SPLICE_VERSION;
   121→}
   122→
   123→// TIME CONVERSION UTILITIES and TIMECODE FORMAT are now in jsxUtils.jsx (included above)
   124→
   125→// ============================================================================
   126→// SEQUENCE & PROJECT OPERATIONS
   127→// ============================================================================
   128→
   129→function getActiveSequence() {
   130→    var seq = app.project.activeSequence;
   131→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   132→
   133→    return JSON.stringify({
   134→        name: seq.name,
   135→        id: seq.sequenceID,
   136→        duration: seq.end,
   137→        videoTrackCount: seq.videoTracks.numTracks,
   138→        audioTrackCount: seq.audioTracks.numTracks,
   139→        frameRate: seq.getSettings().videoFrameRate.seconds,
   140→        width: seq.frameSizeHorizontal,
   141→        height: seq.frameSizeVertical
   142→    });
   143→}
   144→
   145→function getSequenceSettings() {
   146→    var seq = app.project.activeSequence;
   147→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   148→
   149→    var settings = seq.getSettings();
   150→    return JSON.stringify({
   151→        frameRate: settings.videoFrameRate.seconds,
   152→        width: settings.videoFrameWidth,
   153→        height: settings.videoFrameHeight,
   154→        displayFormat: settings.videoDisplayFormat
   155→    });
   156→}
   157→
   158→function cloneSequence(newName) {
   159→    var seq = app.project.activeSequence;
   160→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   161→
   162→    try {
   163→        // Clone the sequence
   164→        seq.clone();
   165→
   166→        // Find the cloned sequence (last one with matching name pattern)
   167→        var clonedSeq = null;
   168→        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
   169→            var s = app.project.sequences[i];
   170→            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
   171→                clonedSeq = s;
   172→                break;
   173→            }
   174→        }
   175→
   176→        if (clonedSeq && newName) {
   177→            clonedSeq.name = newName;
   178→        }
   179→
   180→        return JSON.stringify({
   181→            success: true,
   182→            sequenceId: clonedSeq ? clonedSeq.sequenceID : null,
   183→            name: clonedSeq ? clonedSeq.name : newName
   184→        });
   185→    } catch (e) {
   186→        return JSON.stringify({ error: e.message });
   187→    }
   188→}
   189→
   190→function createNewSequence(name) {
   191→    try {
   192→        app.project.createNewSequence(name);
   193→        var seq = app.project.activeSequence;
   194→        return JSON.stringify({
   195→            success: true,
   196→            sequenceId: seq.sequenceID,
   197→            name: seq.name
   198→        });
   199→    } catch (e) {
   200→        return JSON.stringify({ error: e.message });
   201→    }
   202→}
   203→
   204→function setActiveSequence(sequenceId) {
   205→    for (var i = 0; i < app.project.sequences.numSequences; i++) {
   206→        if (app.project.sequences[i].sequenceID === sequenceId) {
   207→            app.project.activeSequence = app.project.sequences[i];
   208→            return JSON.stringify({ success: true });
   209→        }
   210→    }
   211→    return JSON.stringify({ error: "Sequence not found" });
   212→}
   213→
   214→/**
   215→ * Seek playhead to a specific time in seconds
   216→ */
   217→function seekToTime(seconds) {
   218→    var seq = app.project.activeSequence;
   219→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   220→
   221→    try {
   222→        // Convert seconds to ticks
   223→        var ticks = seconds * TICKS_PER_SECOND;
   224→
   225→        // Create a Time object and set position
   226→        var playerPos = seq.getPlayerPosition();
   227→        playerPos.seconds = seconds;
   228→        seq.setPlayerPosition(playerPos.ticks.toString());
   229→
   230→        return JSON.stringify({ success: true, position: seconds });
   231→    } catch (e) {
   232→        return JSON.stringify({ error: e.message });
   233→    }
   234→}
   235→
   236→/**
   237→ * Get current playhead position in seconds
   238→ */
   239→function getPlayerPosition() {
   240→    var seq = app.project.activeSequence;
   241→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   242→
   243→    try {
   244→        var pos = seq.getPlayerPosition();
   245→        return JSON.stringify({
   246→            ticks: pos.ticks,
   247→            seconds: pos.seconds
   248→        });
   249→    } catch (e) {
   250→        return JSON.stringify({ error: e.message });
   251→    }
   252→}
   253→
   254→// ============================================================================
   255→// RAZOR OPERATIONS
   256→// ============================================================================
   257→
   258→function razorSequenceAtFrames(frames) {
   259→    if (!isQEAvailable() && !enableQE()) {
   260→        return JSON.stringify({ error: "QE not available for razor operation" });
   261→    }
   262→    try {
   263→        frames = String(frames);
   264→        setTimecodeToFrames();
   265→        qe.project.getActiveSequence().razor(frames);
   266→        return JSON.stringify({ success: true });
   267→    } catch (e) {
   268→        return JSON.stringify({ error: "Razor failed: " + e.message });
   269→    }
   270→}
   271→
   272→function razorSequenceAtSeconds(seconds) {
   273→    try {
   274→        var frames = parseInt(Math.round(seconds / app.project.activeSequence.getSettings().videoFrameRate.seconds)).toString();
   275→        return razorSequenceAtFrames(frames);
   276→    } catch (e) {
   277→        return JSON.stringify({ error: "Razor at seconds failed: " + e.message });
   278→    }
   279→}
   280→
   281→function razorSequenceAtFramesArray(silences_array) {
   282→    if (!isQEAvailable() && !enableQE()) {
   283→        return JSON.stringify({ error: "QE not available for razor operation" });
   284→    }
   285→    try {
   286→        setTimecodeToFrames();
   287→        try {
   288→            silences_array = JSON.parse(silences_array);
   289→        } catch (e) {
   290→            return JSON.stringify({ error: "Invalid JSON input: " + e.message });
   291→        }
   292→        var razorCount = 0;
   293→        for (var i = 0; i < silences_array.length; i++) {
   294→            for (var j = 0; j < silences_array[i].length; j++) {
   295→                var frames = String(silences_array[i][j]);
   296→                qe.project.getActiveSequence().razor(frames);
   297→                razorCount++;
   298→            }
   299→        }
   300→        return JSON.stringify({ success: true, razorCount: razorCount });
   301→    } catch (e) {
   302→        return JSON.stringify({ error: "Razor array failed: " + e.message });
   303→    }
   304→}
   305→
   306→function razorTrackAtFrames(trackType, trackIndex, frames) {
   307→    if (!isQEAvailable() && !enableQE()) {
   308→        return JSON.stringify({ error: "QE not available for track razor" });
   309→    }
   310→    try {
   311→        frames = String(frames);
   312→        setTimecodeToFrames();
   313→        if (trackType == "video") {
   314→            qe.project.getActiveSequence().getVideoTrackAt(trackIndex).razor(frames);
   315→        } else if (trackType == "audio") {
   316→            qe.project.getActiveSequence().getAudioTrackAt(trackIndex).razor(frames);
   317→        }
   318→        return JSON.stringify({ success: true });
   319→    } catch (e) {
   320→        return JSON.stringify({ error: "Track razor failed: " + e.message });
   321→    }
   322→}
   323→
   324→function razorTrackAtSeconds(trackType, trackIndex, seconds) {
   325→    try {
   326→        var frames = parseInt(Math.round(seconds / app.project.activeSequence.getSettings().videoFrameRate.seconds)).toString();
   327→        return razorTrackAtFrames(trackType, trackIndex, frames);
   328→    } catch (e) {
   329→        return JSON.stringify({ error: "Track razor at seconds failed: " + e.message });
   330→    }
   331→}
   332→
   333→// ============================================================================
   334→// CLIP OPERATIONS
   335→// ============================================================================
   336→
   337→function getClipsInTrack(trackIndex, trackType) {
   338→    var clips = [];
   339→    if (trackType == "audio") {
   340→        for (var i = 0; i < app.project.activeSequence.audioTracks[trackIndex].clips.numItems; i++) {
   341→            var clip = app.project.activeSequence.audioTracks[trackIndex].clips[i];
   342→            clips.push({
   343→                index: i,
   344→                name: clip.name,
   345→                start: clip.start.seconds,
   346→                end: clip.end.seconds,
