   954→
   955→function buildSequenceFromCutList(cutListJson) {
   956→    try {
   957→        var cutList;
   958→        try {
   959→            cutList = JSON.parse(cutListJson);
   960→        } catch (e) {
   961→            return JSON.stringify({ error: "Invalid JSON input: " + e.message });
   962→        }
   963→        var seq = app.project.activeSequence;
   964→        if (!seq) return JSON.stringify({ error: "No active sequence" });
   965→
   966→        // Clone the sequence
   967→        var newSequenceName = seq.name + "_SPLICE";
   968→        seq.clone();
   969→
   970→        // Find the cloned sequence
   971→        var newSeq = null;
   972→        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
   973→            var s = app.project.sequences[i];
   974→            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
   975→                newSeq = s;
   976→                break;
   977→            }
   978→        }
   979→
   980→        if (!newSeq) {
   981→            return JSON.stringify({ error: "Failed to clone sequence" });
   982→        }
   983→
   984→        newSeq.name = newSequenceName;
   985→        app.project.activeSequence = newSeq;
   986→
   987→        // Clear all clips from the new sequence
   988→        clearSequence();
   989→
   990→        // Insert segments
   991→        var currentPosition = 0;
   992→        var stats = {
   993→            segmentsInserted: 0,
   994→            totalDuration: 0
   995→        };
   996→
   997→        for (var i = 0; i < cutList.segments.length; i++) {
   998→            var segment = cutList.segments[i];
   999→
  1000→            // Find the source project item
  1001→            var sourceItem = null;
  1002→            if (segment.sourcePath) {
  1003→                sourceItem = findItemByPath(app.project.rootItem, segment.sourcePath);
  1004→            }
  1005→            if (!sourceItem && segment.sourceName) {
  1006→                sourceItem = findItemByName(app.project.rootItem, segment.sourceName);
  1007→            }
  1008→
  1009→            if (!sourceItem) continue;
  1010→
  1011→            // Set color label
  1012→            if (segment.colorHint && SEGMENT_COLORS[segment.colorHint]) {
  1013→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.colorHint]);
  1014→            } else if (segment.type && SEGMENT_COLORS[segment.type]) {
  1015→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.type]);
  1016→            }
  1017→
  1018→            // Insert clip at current position
  1019→            var targetTime = new Time();
  1020→            targetTime.seconds = currentPosition;
  1021→
  1022→            newSeq.videoTracks[0].insertClip(sourceItem, targetTime);
  1023→
  1024→            // Set in/out points
  1025→            var clipDuration = segment.outPoint - segment.inPoint;
  1026→            var videoClips = newSeq.videoTracks[0].clips;
  1027→            var insertedClip = videoClips[videoClips.numItems - 1];
  1028→
  1029→            if (insertedClip) {
  1030→                var inTime = new Time();
  1031→                inTime.seconds = segment.inPoint;
  1032→                insertedClip.inPoint = inTime;
  1033→
  1034→                var outTime = new Time();
  1035→                outTime.seconds = segment.outPoint;
  1036→                insertedClip.outPoint = outTime;
  1037→
  1038→                // Set clip name if takeLabel provided
  1039→                if (segment.takeLabel) {
  1040→                    insertedClip.name = segment.takeLabel;
  1041→                }
  1042→            }
  1043→
  1044→            currentPosition += clipDuration;
  1045→            stats.segmentsInserted++;
  1046→            stats.totalDuration += clipDuration;
  1047→        }
  1048→
  1049→        return JSON.stringify({
  1050→            success: true,
  1051→            sequenceName: newSequenceName,
  1052→            stats: stats
  1053→        });
  1054→    } catch (e) {
  1055→        return JSON.stringify({ error: e.message });
  1056→    }
  1057→}
