   399→/**
   400→ * Check if a sequence is currently open in the Timeline
   401→ * Enhanced with logging and retry logic
   402→ * @param {number} retries - Number of retry attempts (default: 2)
   403→ * @param {number} delayMs - Delay between retries in ms (default: 300)
   404→ * @returns {Promise<boolean>} true if sequence is open
   405→ */
   406→async function checkSequenceOpen(retries = 2, delayMs = 300) {
   407→    console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC START ==========');
   408→    console.log('[SPLICE] checkSequenceOpen: retries=' + retries + ', delayMs=' + delayMs);
   409→    console.log('[SPLICE] checkSequenceOpen: window.spliceState.activeSequence =', window.spliceState.activeSequence);
   410→
   411→    for (let attempt = 0; attempt <= retries; attempt++) {
   412→        try {
   413→            console.log('[SPLICE] checkSequenceOpen: Attempt ' + (attempt + 1) + ' of ' + (retries + 1));
   414→            console.log('[SPLICE] checkSequenceOpen: Calling jsx.call("checkSequenceOpen")...');
   415→            const result = await jsx.call('checkSequenceOpen');
   416→            console.log('[SPLICE] checkSequenceOpen: JSX call returned');
   417→            console.log('[SPLICE] checkSequenceOpen: result =', result, '(type:', typeof result + ')');
   418→            debugLog('checkSequenceOpen attempt', attempt + 1, 'result:', result, 'type:', typeof result);
   419→
   420→            if (result === true || result === 'true') {
   421→                console.log('[SPLICE] checkSequenceOpen: Returning TRUE - sequence is open');
   422→                console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (TRUE) ==========');
   423→                return true;
   424→            }
   425→
   426→            // If false, wait and retry (sequence might be loading)
   427→            if (attempt < retries) {
   428→                console.log('[SPLICE] checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   429→                debugLog('checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   430→                await new Promise(r => setTimeout(r, delayMs));
   431→            }
   432→        } catch (error) {
   433→            console.error('[SPLICE] checkSequenceOpen: Attempt', attempt + 1, 'failed with exception:', error.message);
   434→            console.error('[SPLICE] checkSequenceOpen: Error stack:', error.stack);
   435→
   436→            // On last attempt, return false but log the actual error
   437→            if (attempt === retries) {
   438→                console.error('[SPLICE] checkSequenceOpen: All attempts failed. Last error:', error.message);
   439→                // Store error for diagnostics
   440→                window.spliceState.lastSequenceCheckError = error.message;
   441→                console.log('[SPLICE] checkSequenceOpen: Stored error in window.spliceState.lastSequenceCheckError:', error.message);
   442→                console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (ERROR) ==========');
   443→                return false;
   444→            }
   445→
   446→            // Wait before retry
   447→            console.log('[SPLICE] checkSequenceOpen: Waiting', delayMs, 'ms before retry...');
   448→            await new Promise(r => setTimeout(r, delayMs));
   449→        }
   450→    }
   451→    console.log('[SPLICE] checkSequenceOpen: All attempts completed without finding sequence');
   452→    console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (FALSE) ==========');
   453→    return false;
   454→}
