   851→            }
   852→        }
   853→
   854→        if (!audioFilePath) {
   855→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   856→        }
   857→
   858→        // Store audio path globally for other modules
   859→        currentAudioPath = audioFilePath;
   860→        window.spliceState.audioPath = audioFilePath;
   861→
   862→        // Call backend for silence detection with audio file path
   863→        updateProgress(30, 'Detecting silences...');
   864→        const silenceResponse = await fetchWithTimeout(
   865→            `${getBackendUrl()}/silences-rms`,
   866→            {
   867→                method: 'POST',
   868→                headers: getAuthHeaders(),
