   897→                    }
   898→                );
   899→
   900→                if (analyzeResponse.ok) {
   901→                    const analyzeData = await analyzeResponse.json();
   902→                    previewTakes = analyzeData.takes || [];
   903→
   904→                    // Store transcript for captions/music modules
   905→                    if (analyzeData.transcript) {
   906→                        currentTranscript = analyzeData.transcript;
   907→                        window.currentTranscript = analyzeData.transcript;
   908→                        window.spliceState.lastTranscript = analyzeData.transcript;
   909→                    }
   910→                } else {
   911→                    // Handle specific error codes
   912→                    const errorMsg = await parseErrorResponse(analyzeResponse);
   913→                    if (analyzeResponse.status === 402) {
   914→                        console.warn('[SPLICE] Takes detection skipped: insufficient credits');
   915→                        setStatus('Takes detection skipped: insufficient credits', true);
   916→                    } else if (analyzeResponse.status === 401) {
