  1325→async function buildSequence() {
  1326→    if (isOperationInProgress) return;
  1327→
  1328→    isOperationInProgress = true;
  1329→    setStatus('Building sequence...');
  1330→
  1331→    try {
  1332→        const settings = getSettings();
  1333→
  1334→        // Ensure we have sequence info
  1335→        if (!currentSequenceInfo) {
  1336→            currentSequenceInfo = await jsx.call('getActiveSequence');
  1337→        }
  1338→
  1339→        // Create cut list from selected silences
  1340→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
  1341→
  1342→        // Calculate total duration from sequence info or last silence
  1343→        let duration = 0;
  1344→        if (currentSequenceInfo && currentSequenceInfo.duration) {
  1345→            duration = currentSequenceInfo.duration;
  1346→        } else if (previewSilences.length > 0) {
  1347→            const lastSilence = previewSilences[previewSilences.length - 1];
  1348→            duration = lastSilence.end + 10; // Add buffer
  1349→        }
  1350→
  1351→        // Call backend to generate cut list
  1352→        const cutListResponse = await fetchWithTimeout(
  1353→            `${getBackendUrl()}/cut-list`,
  1354→            {
  1355→                method: 'POST',
  1356→                headers: getAuthHeaders(),
  1357→                body: JSON.stringify({
  1358→                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
  1359→                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
  1360→                    duration: duration,
  1361→                    silences: selectedSilences,
  1362→                    takes: previewTakes,
  1363→                    enableTakes: settings.enableTakesDetection,
  1364→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
  1365→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
  1366→                })
  1367→            }
  1368→        );
  1369→
  1370→        if (!cutListResponse.ok) {
  1371→            throw new Error(await parseErrorResponse(cutListResponse));
  1372→        }
  1373→
  1374→        const cutList = await cutListResponse.json();
  1375→
  1376→        // Build sequence via JSX
  1377→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
  1378→
  1379→        if (result && result.success) {
  1380→            setStatus(`Built sequence: ${result.sequenceName}`);
  1381→            hidePreview();
  1382→        } else {
  1383→            throw new Error(result?.error || 'Build failed');
  1384→        }
  1385→    } catch (error) {
  1386→        setStatus('Build failed: ' + error.message, true);
  1387→    } finally {
  1388→        isOperationInProgress = false;
  1389→    }
  1390→}
