  1616→async function activateLicense() {
  1617→    if (!ui.licenseKeyInput) return;
  1618→
  1619→    const licenseKey = ui.licenseKeyInput.value.trim();
  1620→    if (!licenseKey) {
  1621→        setStatus('Please enter a license key', true);
  1622→        return;
  1623→    }
  1624→
  1625→    try {
  1626→        const response = await fetchWithTimeout(
  1627→            `${getBackendUrl()}/license/activate`,
  1628→            {
  1629→                method: 'POST',
  1630→                headers: { 'Content-Type': 'application/json' },
  1631→                body: JSON.stringify({ key: licenseKey })
  1632→            }
  1633→        );
  1634→
  1635→        if (!response.ok) {
  1636→            throw new Error(await parseErrorResponse(response));
  1637→        }
  1638→
  1639→        const data = await response.json();
  1640→        saveSettings({
  1641→            customerId: data.customerId,
  1642→            licenseKey: licenseKey
  1643→        });
  1644→
  1645→        if (ui.loginModal) ui.loginModal.classList.add('hidden');
  1646→        setStatus('License activated');
  1647→        updateCredits();
  1648→    } catch (error) {
  1649→        setStatus('Activation failed: ' + error.message, true);
  1650→    }
  1651→}
