  1653→/**
  1654→ * Look up license key by email address
  1655→ */
  1656→async function lookupLicense() {
  1657→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1658→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1659→
  1660→    if (!lookupEmailInput) return;
  1661→
  1662→    const email = lookupEmailInput.value.trim().toLowerCase();
  1663→    if (!email) {
  1664→        setStatus('Please enter your email address', true);
  1665→        return;
  1666→    }
  1667→
  1668→    // Basic email validation
  1669→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1670→    if (!emailRegex.test(email)) {
  1671→        setStatus('Please enter a valid email address', true);
  1672→        return;
  1673→    }
  1674→
  1675→    const lookupBtn = document.getElementById('lookupLicenseBtn');
  1676→    if (lookupBtn) {
  1677→        lookupBtn.disabled = true;
  1678→        lookupBtn.textContent = 'Looking up...';
  1679→    }
  1680→
  1681→    try {
  1682→        const response = await fetchWithTimeout(
  1683→            `${getBackendUrl()}/license/lookup`,
  1684→            {
  1685→                method: 'POST',
  1686→                headers: { 'Content-Type': 'application/json' },
  1687→                body: JSON.stringify({ email })
  1688→            },
  1689→            30000
  1690→        );
  1691→
  1692→        const result = await response.json();
  1693→
  1694→        // Note: For security, the backend does NOT return the license key directly.
  1695→        // It sends the key via email to prevent enumeration attacks.
  1696→        if (result.success) {
  1697→            setStatus(result.message || 'If a license exists, it has been sent to your email.');
  1698→        } else {
  1699→            setStatus(result.error || 'Lookup failed. Please try again.', true);
  1700→        }
  1701→    } catch (error) {
  1702→        setStatus('Lookup failed: ' + error.message, true);
  1703→    } finally {
  1704→        if (lookupBtn) {
  1705→            lookupBtn.disabled = false;
  1706→            lookupBtn.textContent = 'Lookup';
  1707→        }
  1708→    }
  1709→}
