  1820→async function runDiagnostics() {
  1821→    if (!ui.debugLogs || !ui.debugQuickDiagnosis) return;
  1822→
  1823→    diagnosticLogs = [];
  1824→    ui.debugLogs.innerHTML = '<p class="debug-running">Running diagnostics... Please wait...</p>';
  1825→
  1826→    try {
  1827→        // Test 0: Environment Info (using compatibility module)
  1828→        addDiagnosticLog('=== Environment Detection ===');
  1829→        if (typeof SpliceCompatibility !== 'undefined') {
  1830→            const env = SpliceCompatibility.detectEnvironment();
  1831→            addDiagnosticLog('Platform: ' + env.platform.os + ' ' + env.platform.osVersion);
  1832→            addDiagnosticLog('Architecture: ' + env.platform.architecture + (env.platform.isAppleSilicon ? ' (Apple Silicon)' : ''));
  1833→            if (env.cep.isRunning) {
  1834→                addDiagnosticLog('CEP Version: ' + env.cep.version);
  1835→                addDiagnosticLog('Host: ' + env.host.name + ' ' + env.host.version);
  1836→            }
  1837→            addDiagnosticLog('Chromium: ' + (env.browser.chromiumVersion || 'N/A'));
  1838→        } else {
  1839→            addDiagnosticLog('Compatibility module not loaded');
  1840→        }
  1841→        addDiagnosticLog('');
  1842→
  1843→        // Test 0.1: Check CEP environment
  1844→        addDiagnosticLog('Test 0: Checking CEP environment...');
  1845→        if (window.__adobe_cep__) {
  1846→            addDiagnosticLog('✓ CEP runtime detected (window.__adobe_cep__ exists)');
  1847→        } else {
  1848→            addDiagnosticLog('✗ CEP runtime NOT detected - running in browser mode');
  1849→        }
  1850→
  1851→        // Test 0.5: Basic ExtendScript test (doesn't require hostScript.jsx)
  1852→        addDiagnosticLog('Test 0.5: Testing basic ExtendScript...');
  1853→        try {
  1854→            const basicResult = await jsx.evalScript('1 + 1');
  1855→            addDiagnosticLog('✓ Basic ExtendScript works: 1+1 = ' + basicResult);
  1856→        } catch (e) {
  1857→            addDiagnosticLog('✗ Basic ExtendScript FAILED: ' + e.message);
  1858→            addDiagnosticLog('  → This means Premiere Pro cannot run ANY ExtendScript');
  1859→            addDiagnosticLog('  → Make sure CEP debug mode is enabled and Premiere was restarted');
  1860→        }
  1861→
  1862→        // Test 0.6: Check if hostScript.jsx functions are loaded
  1863→        addDiagnosticLog('Test 0.6: Checking if hostScript.jsx is loaded...');
  1864→        try {
  1865→            const typeofGetVersion = await jsx.evalScript('typeof getVersion');
  1866→            if (typeofGetVersion === 'function') {
  1867→                addDiagnosticLog('✓ hostScript.jsx is loaded (getVersion exists)');
  1868→            } else {
  1869→                addDiagnosticLog('✗ hostScript.jsx NOT loaded (getVersion type: ' + typeofGetVersion + ')');
  1870→                addDiagnosticLog('  → The JSX file may not be in the correct location');
  1871→            }
  1872→        } catch (e) {
  1873→            addDiagnosticLog('✗ hostScript check FAILED: ' + e.message);
  1874→        }
  1875→
  1876→        // Test 1: JSX Connection
  1877→        addDiagnosticLog('Test 1: Checking JSX connection...');
  1878→        try {
  1879→            const version = await jsx.call('getVersion');
  1880→            addDiagnosticLog('✓ JSX connection OK, version: ' + version);
  1881→        } catch (e) {
  1882→            addDiagnosticLog('✗ JSX connection FAILED: ' + e.message);
  1883→        }
  1884→
  1885→        // Test 2: Check for active sequence
  1886→        addDiagnosticLog('Test 2: Checking for active sequence...');
  1887→        try {
  1888→            const hasSequence = await checkSequenceOpen(0, 0); // No retries for diagnostics
  1889→            addDiagnosticLog('✓ checkSequenceOpen() returned: ' + hasSequence);
  1890→        } catch (e) {
  1891→            addDiagnosticLog('✗ checkSequenceOpen() FAILED: ' + e.message);
  1892→        }
  1893→
  1894→        // Test 3: Get sequence info
  1895→        addDiagnosticLog('Test 3: Getting sequence info...');
  1896→        try {
  1897→            const seqInfo = await jsx.call('getActiveSequence');
  1898→            if (seqInfo && !seqInfo.error) {
  1899→                addDiagnosticLog('✓ Sequence found: ' + (seqInfo.name || 'Unknown'));
  1900→                addDiagnosticLog('  - Sequence ID: ' + (seqInfo.sequenceID || 'N/A'));
  1901→                addDiagnosticLog('  - Duration: ' + (seqInfo.duration || 'N/A'));
  1902→                addDiagnosticLog('  - Frame size: ' + (seqInfo.frameSizeHorizontal || 'N/A') + 'x' + (seqInfo.frameSizeVertical || 'N/A'));
  1903→            } else {
  1904→                addDiagnosticLog('✗ No active sequence found');
  1905→            }
  1906→        } catch (e) {
  1907→            addDiagnosticLog('✗ getActiveSequence() FAILED: ' + e.message);
  1908→        }
  1909→
  1910→        // Test 4: Check project sequences
  1911→        addDiagnosticLog('Test 4: Checking all project sequences...');
  1912→        try {
  1913→            const sequences = await jsx.call('getAllSequences');
  1914→            if (sequences && sequences.length > 0) {
  1915→                addDiagnosticLog('✓ Total sequences in project: ' + sequences.length);
  1916→                sequences.forEach((seq, i) => {
  1917→                    addDiagnosticLog('  - Sequence ' + i + ': ' + (seq.name || 'Unnamed'));
  1918→                });
  1919→            } else {
  1920→                addDiagnosticLog('✗ No sequences found in project');
  1921→            }
  1922→        } catch (e) {
  1923→            addDiagnosticLog('✗ getAllSequences() FAILED: ' + e.message);
  1924→        }
  1925→
  1926→        // Test 5: Check for active sequence via DOM
  1927→        addDiagnosticLog('Test 5: Checking app.project.activeSequence...');
  1928→        try {
  1929→            const result = await jsx.evalScript(`
  1930→                (function() {
  1931→                    try {
  1932→                        if (app && app.project && app.project.activeSequence) {
  1933→                            return JSON.stringify({
  1934→                                success: true,
  1935→                                name: app.project.activeSequence.name,
  1936→                                id: app.project.activeSequence.sequenceID
  1937→                            });
  1938→                        } else {
  1939→                            return JSON.stringify({
  1940→                                success: false,
  1941→                                reason: app ? (app.project ? 'app.project.activeSequence is null' : 'app.project is null') : 'app is null'
  1942→                            });
  1943→                        }
  1944→                    } catch (e) {
  1945→                        return JSON.stringify({ success: false, reason: 'Error: ' + e.message });
  1946→                    }
  1947→                })()
  1948→            `);
  1949→            const data = JSON.parse(result);
  1950→            if (data.success) {
  1951→                addDiagnosticLog('✓ app.project.activeSequence found: ' + data.name + ' (ID: ' + data.id + ')');
  1952→            } else {
  1953→                addDiagnosticLog('✗ app.project.activeSequence NOT found: ' + data.reason);
  1954→            }
  1955→        } catch (e) {
  1956→            addDiagnosticLog('✗ DOM check FAILED: ' + e.message);
  1957→        }
  1958→
  1959→        // Test 6: Check QE DOM availability
  1960→        addDiagnosticLog('Test 6: Checking QE DOM availability...');
  1961→        try {
  1962→            const qeResult = await jsx.evalScript(`
  1963→                (function() {
  1964→                    try {
  1965→                        if (typeof qe !== 'undefined' && qe !== null) {
  1966→                            return JSON.stringify({ available: true });
  1967→                        } else {
  1968→                            return JSON.stringify({ available: false });
  1969→                        }
  1970→                    } catch (e) {
  1971→                        return JSON.stringify({ available: false, error: e.message });
  1972→                    }
  1973→                })()
  1974→            `);
  1975→            const qeData = JSON.parse(qeResult);
  1976→            if (qeData.available) {
  1977→                addDiagnosticLog('✓ QE DOM is available');
  1978→            } else {
