   243→/**
   244→ * Post-initialization diagnostic to verify all critical UI elements
   245→ * are properly cached and event listeners are attached
   246→ */
   247→function verifyUIInitialization() {
   248→    const criticalElements = {
   249→        'goBtn': { el: ui.goBtn, desc: 'GO button (main action)' },
   250→        'settingsBtn': { el: ui.settingsBtn, desc: 'Settings button' },
   251→        'creditBadge': { el: ui.creditBadge, desc: 'Credit badge' },
   252→        'status': { el: ui.status, desc: 'Status bar' },
   253→        'optionsToggle': { el: ui.optionsToggle, desc: 'Options toggle' },
   254→        'optionsPanel': { el: ui.optionsPanel, desc: 'Options panel' }
   255→    };
   256→
   257→    const missingElements = [];
   258→    const presentElements = [];
   259→
   260→    Object.entries(criticalElements).forEach(([id, { el, desc }]) => {
   261→        if (!el) {
   262→            missingElements.push(`#${id} (${desc})`);
   263→        } else {
   264→            presentElements.push(id);
   265→        }
   266→    });
   267→
   268→    if (missingElements.length > 0) {
   269→        console.error('[SPLICE] ⚠️ UI INITIALIZATION INCOMPLETE');
   270→        console.error('[SPLICE] Missing critical elements:', missingElements);
   271→        console.error('[SPLICE] Panel may not function correctly. Check index.html structure.');
   272→
   273→        // Show error in status bar if available
   274→        const statusEl = document.getElementById('status');
   275→        if (statusEl) {
   276→            statusEl.textContent = 'Init error: ' + missingElements.length + ' UI elements missing';
   277→            statusEl.classList.add('error');
   278→        }
   279→    } else {
   280→        console.log('[SPLICE] ✓ All critical UI elements initialized:', presentElements.join(', '));
   281→    }
   282→
   283→    // Verify GO button has click listener
   284→    if (ui.goBtn) {
   285→        const listeners = getEventListeners ? getEventListeners(ui.goBtn) : null;
   286→        if (!listeners || !listeners.click || listeners.click.length === 0) {
   287→            // Can't check in production, but log that we tried
   288→            console.log('[SPLICE] GO button present - click handler should be attached');
   289→        }
   290→    }
   291→}
