   296→/**
   297→ * Initialize event listeners for sequence state changes
   298→ * This provides real-time updates when sequences are activated/deactivated
   299→ */
   300→function initSequenceEventListeners() {
   301→    if (!jsx.cs) {
   302→        console.warn('[SPLICE] Cannot init sequence listeners - CSInterface not available');
   303→        return;
   304→    }
   305→
   306→    try {
   307→        // Listen for sequence activation events
   308→        jsx.cs.addEventListener('com.adobe.csxs.events.SequenceActivated', async (event) => {
   309→            debugLog('Sequence activated event received');
   310→            try {
   311→                const seqInfo = await jsx.call('getActiveSequence');
   312→                if (seqInfo && !seqInfo.error) {
   313→                    window.spliceState.activeSequence = seqInfo;
   314→                    setStatus('Sequence: ' + (seqInfo.name || 'Unknown'));
   315→                    debugLog('Active sequence updated:', seqInfo.name);
   316→                }
   317→            } catch (e) {
   318→                console.warn('[SPLICE] Failed to get sequence info on activation:', e.message);
   319→            }
   320→        });
   321→
   322→        // Listen for sequence close/deactivation
   323→        jsx.cs.addEventListener('com.adobe.csxs.events.SequenceDeactivated', () => {
   324→            debugLog('Sequence deactivated event');
   325→            window.spliceState.activeSequence = null;
   326→            setStatus('No sequence open', true);
   327→        });
   328→
   329→        // Listen for project item selection changes (includes sequences)
   330→        jsx.cs.addEventListener('com.adobe.csxs.events.SelectionChanged', async () => {
   331→            // Debounce rapid selection changes
   332→            if (window._selectionChangeTimeout) {
   333→                clearTimeout(window._selectionChangeTimeout);
   334→            }
   335→            // FIX: Increased debounce to 500ms to allow Premiere Pro state to settle
   336→            window._selectionChangeTimeout = setTimeout(async () => {
   337→                try {
   338→                    // FIX: Use retries (2 attempts, 300ms delay) to handle timing issues
   339→                    // Previous code used (0, 0) which caused race conditions
   340→                    const hasSequence = await checkSequenceOpen(2, 300);
   341→                    if (hasSequence) {
   342→                        const seqInfo = await jsx.call('getActiveSequence');
   343→                        if (seqInfo && !seqInfo.error) {
   344→                            // Check if sequence actually changed by comparing IDs
   345→                            const currentId = window.spliceState.activeSequence ? window.spliceState.activeSequence.id : null;
   346→
   347→                            // Always update if ID changed or we didn't have a sequence before
   348→                            if (currentId !== seqInfo.id) {
   349→                                window.spliceState.activeSequence = seqInfo;
   350→                                setStatus('Sequence: ' + (seqInfo.name || 'Unknown'));
   351→                                debugLog('Active sequence changed:', seqInfo.name, '(ID: ' + seqInfo.id + ')');
   352→                            }
   353→                        }
   354→                    } else {
   355→                        // If we had a sequence but now don't, likely closed or lost focus
   356→                        if (window.spliceState.activeSequence) {
   357→                            debugLog('Sequence check failed - potentially lost focus');
   358→                            // We don't verify strict nulling here to avoid flickering, 
   359→                            // as SequenceDeactivated event should handle the explicit close.
   360→                        }
   361→                    }
   362→                } catch (e) {
   363→                    // FIX: Log errors instead of silently ignoring
   364→                    console.warn('[SPLICE] Selection change handler error:', e.message);
   365→                    debugLog('Selection change failed:', e.message);
   366→                }
   367→            }, 500);
   368→        });
   369→
   370→        debugLog('Sequence event listeners initialized');
   371→    } catch (e) {
   372→        console.error('[SPLICE] Failed to init sequence event listeners:', e.message);
   373→    }
   374→}
