   386→        const errorDetail = e.message || 'Unknown error';
   387→        setStatus(`Premiere Pro connection failed: ${errorDetail}`, true);
   388→
   389→        // Also try to log to a visible area if possible (detection log)
   390→        const logArea = document.getElementById('detection-log');
   391→        if (logArea) {
   392→            logArea.innerHTML += `<div class="error-item">JSX Error: ${errorDetail}</div>`;
   393→        }
   394→
   395→        return false;
   396→    }
   397→}
