  1100→function renderPreviewList() {
  1101→    if (!ui.previewList) return;
  1102→
  1103→    const html = [];
  1104→
  1105→    // Render silences
  1106→    previewSilences.forEach((silence, i) => {
  1107→        const isSelected = selectedSilenceIndices.has(i);
  1108→        const duration = silence.end - silence.start;
  1109→        html.push(`
  1110→            <div class="preview-item ${isSelected ? '' : 'excluded'}" data-index="${i}" data-type="silence">
  1111→                <input type="checkbox" class="preview-item-check" ${isSelected ? 'checked' : ''}>
  1112→                <div class="preview-item-info">
  1113→                    <div class="preview-item-time">${formatTime(silence.start)} - ${formatTime(silence.end)}</div>
  1114→                    <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
  1115→                </div>
  1116→            </div>
  1117→        `);
  1118→    });
  1119→
  1120→    // Render takes
  1121→    previewTakes.forEach((take, i) => {
  1122→        // SECURITY: Escape take label to prevent XSS
  1123→        const safeLabel = escapeHtml(take.label || 'Take ' + (take.takeNumber || i + 1));
  1124→        html.push(`
  1125→            <div class="preview-item take-item" data-index="${i}" data-type="take">
  1126→                <div class="preview-item-icon">🎬</div>
  1127→                <div class="preview-item-info">
  1128→                    <div class="preview-item-time">${formatTime(take.start)} - ${formatTime(take.end)}</div>
  1129→                    <div class="preview-item-label">${safeLabel}</div>
  1130→                </div>
  1131→            </div>
