  1734→                headers: getAuthHeaders()
  1735→            }
  1736→        );
  1737→
  1738→        if (response.ok) {
  1739→            const data = await response.json();
  1740→            // Backend returns hoursRemaining (not remainingMinutes)
  1741→            const hours = (data.hoursRemaining || 0).toFixed(1);
  1742→            ui.creditBadge.textContent = `${hours}h`;
  1743→            // Low warning when less than 1 hour remaining
  1744→            ui.creditBadge.className = data.hoursRemaining < 1 ? 'credit-badge low' : 'credit-badge ok';
  1745→            ui.creditBadge.style.display = 'flex';
  1746→        } else {
  1747→            ui.creditBadge.className = 'credit-badge error';
  1748→            ui.creditBadge.textContent = 'Error';
  1749→            ui.creditBadge.style.display = 'flex';
  1750→        }
  1751→    } catch {
  1752→        ui.creditBadge.className = 'credit-badge error';
  1753→        ui.creditBadge.textContent = 'Offline';
  1754→        ui.creditBadge.style.display = 'flex';
  1755→    }
  1756→}
  1757→
