  1800→    // Run diagnostics button
  1801→    if (ui.runDiagnosticsBtn) {
  1802→        ui.runDiagnosticsBtn.addEventListener('click', async () => {
  1803→            await runDiagnostics();
  1804→        });
  1805→    }
