  1807→    // Copy diagnostics button
  1808→    if (ui.copyDiagnosticsBtn) {
  1809→        ui.copyDiagnosticsBtn.addEventListener('click', () => {
  1810→            copyDiagnosticsToClipboard();
  1811→        });
  1812→    }
