  2036→function addDiagnosticLog(message) {
  2037→    diagnosticLogs.push(message);
  2038→    console.log('[SPLICE DEBUG] ' + message);
  2039→}
  2040→
  2041→function displayDiagnosticLogs() {
  2042→    if (!ui.debugLogs) return;
  2043→
  2044→    const html = diagnosticLogs.map(log => {
