  2051→function updateQuickDiagnosis() {
  2052→    if (!ui.debugQuickDiagnosis) return;
  2053→
  2054→    const logs = diagnosticLogs.join(' ');
  2055→    let diagnosis = '';
  2056→    let solution = '';
