  2059→    if (logs.includes('JSX connection FAILED')) {
  2060→        diagnosis = '❌ JSX Connection Failed';
  2061→        solution = 'The panel cannot communicate with Premiere Pro. Try: 1) Close and reopen the panel, 2) Restart Premiere Pro, 3) Check if plugin is properly installed.';
  2062→    } else if (logs.includes('checkSequenceOpen() returned: false') && logs.includes('No active sequence found')) {
  2063→        diagnosis = '❌ No Active Sequence';
  2064→        solution = 'No sequence is active in the Timeline. Double-click a sequence in the Project panel to open it in the Timeline.';
  2065→    } else if (logs.includes('app.project.activeSequence NOT found') && logs.includes('Sequence found')) {
  2066→        diagnosis = '❌ Sequence Not Active';
  2067→        solution = 'A sequence exists but is not the active sequence. Click on the sequence tab in the Timeline to make it active.';
  2068→    } else if (logs.includes('checkSequenceOpen() returned: true') && logs.includes('app.project.activeSequence found')) {
  2069→        diagnosis = '✅ Everything Looks Good';
  2070→        solution = 'The plugin is working correctly. The sequence is detected and active. Try pressing GO again.';
  2071→    } else if (logs.includes('No sequences found in project')) {
  2072→        diagnosis = '❌ No Sequences in Project';
  2073→        solution = 'Create a sequence in Premiere Pro first (File > New > Sequence), then open it in the Timeline.';
  2074→    } else {
