"use client";

import { motion } from "framer-motion";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  ArrowLeft,
  ArrowRight,
  Check,
  Zap,
  Play,
  Settings,
  Music,
} from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";

const steps = [
  {
    number: 1,
    title: "Download SPLICE",
    description: "Download the UXP or CEP version based on your Premiere Pro version.",
    details: [
      "UXP version: Premiere Pro 25.6+ (Modern)",
      "CEP version: Premiere Pro 14.0-25.9 (Recommended)",
      "Both versions have identical features",
    ],
    action: { label: "Download Now", href: "/download" },
  },
  {
    number: 2,
    title: "Install the Plugin",
    description: "Follow the installation instructions for your operating system.",
    details: [
      "UXP: Use Adobe's UXP Developer Tool to load the plugin",
      "CEP: Copy to Extensions folder and enable debug mode",
      "Restart Premiere Pro after installation",
    ],
    action: { label: "Installation Guide", href: "/docs/installation" },
  },
  {
    number: 3,
    title: "Activate Your License",
    description: "Enter your license key to unlock all features.",
    details: [
      "Open SPLICE panel in Window > Extensions",
      "Click the settings icon and enter your license key",
      "Your license activates instantly",
    ],
    action: { label: "Get a License", href: "/pricing" },
  },
  {
    number: 4,
    title: "Start Editing!",
    description: "Open a sequence and click GO to analyze your footage.",
    details: [
      "SPLICE automatically detects silences and takes",
      "Review the analysis in the preview panel",
      "Click Build Sequence to create your edited timeline",
    ],
    action: null,
  },
];

const quickTips = [
  {
    icon: Zap,
    title: "Use Presets",
    description: "Choose from Podcast, Interview, Vlog, or Tutorial presets for optimized settings.",
  },
  {
    icon: Music,
    title: "AI Music",
    description: "Generate custom background music that matches your video's mood and energy.",
  },
  {
    icon: Settings,
    title: "Fine-tune Settings",
    description: "Adjust sensitivity and minimum silence duration for perfect results.",
  },
];

export default function QuickStartPage() {
  return (
    <div className="min-h-screen bg-[#060b14] dark relative overflow-hidden">
      {/* Background decoration */}
      <div className="absolute inset-0 grid-pattern opacity-30 pointer-events-none" />

      <Header />
      <main className="pt-24 pb-16 relative z-10">
        <div className="container mx-auto px-4">
          {/* Back link */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <Link href="/docs" className="inline-flex items-center text-gray-400 hover:text-[#00D4FF] mb-8">
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Documentation
            </Link>
          </motion.div>

          {/* Hero */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="mb-12 max-w-4xl"
          >
            <Badge className="mb-4 bg-[#00D4FF]/10 text-[#00D4FF] border-[#00D4FF]/30">
              5 minute setup
            </Badge>
            <h1 className="text-4xl md:text-5xl font-bold mb-4 text-white">
              Quick Start Guide
            </h1>
            <p className="text-lg text-gray-400">
              Get up and running with SPLICE in just a few minutes. Follow these steps to start editing 10x faster.
            </p>
          </motion.div>

          <div className="grid lg:grid-cols-12 gap-12 items-start">
            {/* Left Column: Steps */}
            <div className="lg:col-span-7">
              {steps.map((step, index) => (
                <motion.div
                  key={step.number}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  className="mb-8"
                >
                  <Card className="bg-[#0d1420] border-gray-800">
                    <CardContent className="p-6">
                      <div className="flex gap-6">
                        {/* Step number */}
                        <div className="flex-shrink-0">
                          <div className="w-10 h-10 rounded-full bg-[#00D4FF] flex items-center justify-center text-black font-bold">
                            {step.number}
                          </div>
                        </div>

                        {/* Content */}
                        <div className="flex-1">
                          <h3 className="text-xl font-semibold text-white mb-2">
                            {step.title}
                          </h3>
                          <p className="text-gray-400 mb-4">{step.description}</p>

                          <ul className="space-y-2 mb-4">
                            {step.details.map((detail, i) => (
                              <li key={i} className="flex items-start gap-2 text-sm text-gray-300">
                                <Check className="w-4 h-4 text-[#00D4FF] mt-0.5 flex-shrink-0" />
                                {detail}
                              </li>
                            ))}
                          </ul>

                          {step.action && (
                            <Button
                              variant="outline"
                              className="border-[#00D4FF]/30 text-[#00D4FF] hover:bg-[#00D4FF]/10"
                              asChild
                            >
                              <Link href={step.action.href}>
                                {step.action.label}
                                <ArrowRight className="w-4 h-4 ml-2" />
                              </Link>
                            </Button>
                          )}
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                </motion.div>
              ))}

              {/* Next steps (Mobile only/Bottom) */}
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.7 }}
                className="mt-8 flex justify-between items-center lg:hidden"
              >
                <Link href="/docs" className="text-gray-400 hover:text-[#00D4FF]">
                  <ArrowLeft className="w-4 h-4 inline mr-2" />
                  Back to Docs
                </Link>
                <Link href="/docs/features" className="text-[#00D4FF] hover:underline">
                  Explore Features
                  <ArrowRight className="w-4 h-4 inline ml-2" />
                </Link>
              </motion.div>
            </div>

            {/* Right Column: Video & Tips (Sticky) */}
            <div className="lg:col-span-5 space-y-8 sticky top-32">
              {/* Video placeholder */}
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.6 }}
              >
                <Card className="bg-[#0d1420] border-gray-800 overflow-hidden">
                  <div className="aspect-video bg-gradient-to-br from-[#0a1929] to-[#0d2137] flex items-center justify-center group cursor-pointer hover:opacity-90 transition-opacity">
                    <div className="text-center">
                      <div className="w-16 h-16 rounded-full bg-[#00D4FF]/20 flex items-center justify-center mx-auto mb-4 group-hover:bg-[#00D4FF]/30 transition-colors">
                        <Play className="w-6 h-6 text-[#00D4FF] ml-1" />
                      </div>
                      <h3 className="text-white font-semibold mb-1">Video Tutorial</h3>
                      <p className="text-gray-400 text-sm">Watch the full setup walkthrough</p>
                    </div>
                  </div>
                </Card>
              </motion.div>

              {/* Quick Tips */}
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.5 }}
              >
                <h2 className="text-xl font-bold text-white mb-4">Pro Tips</h2>
                <div className="space-y-4">
                  {quickTips.map((tip) => (
                    <Card key={tip.title} className="bg-[#0d1420] border-gray-800">
                      <CardContent className="p-4 flex gap-4">
                        <div className="w-10 h-10 rounded-lg bg-[#00D4FF]/10 flex items-center justify-center shrink-0">
                          <tip.icon className="w-5 h-5 text-[#00D4FF]" />
                        </div>
                        <div>
                          <h3 className="font-semibold text-white text-sm mb-1">{tip.title}</h3>
                          <p className="text-xs text-gray-400">{tip.description}</p>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </motion.div>

              {/* Desktop Next Steps */}
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.8 }}
                className="hidden lg:flex justify-between items-center pt-4 border-t border-gray-800"
              >
                <Link href="/docs" className="text-sm text-gray-400 hover:text-[#00D4FF]">
                  <ArrowLeft className="w-3 h-3 inline mr-1" />
                  Docs
                </Link>
                <Link href="/docs/features" className="text-sm text-[#00D4FF] hover:underline">
                  Explore Features
                  <ArrowRight className="w-3 h-3 inline ml-1" />
                </Link>
              </motion.div>
            </div>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

