    80→
    81→function checkSequenceOpen() {
    82→    try {
    83→        // Quick validation checks without logging every step
    84→        if (!app || !app.project || !app.project.sequences) {
    85→            $.writeln('[SPLICE] checkSequenceOpen: ERROR - Premiere Pro app/project not ready');
    86→            return false;
    87→        }
    88→
    89→        // Primary check: Standard DOM activeSequence
    90→        if (app.project.activeSequence) {
    91→            var seq = app.project.activeSequence;
    92→            if (seq.name && seq.sequenceID) {
    93→                $.writeln('[SPLICE] checkSequenceOpen: ✓ Active sequence found: ' + seq.name);
    94→                return true;
    95→            }
    96→        }
    97→
    98→        // Fallback: Try QE DOM if standard DOM failed
    99→        if (enableQE()) {
   100→            try {
   101→                var qeSeq = qe.project.getActiveSequence();
   102→                if (qeSeq && qeSeq.name) {
   103→                    $.writeln('[SPLICE] checkSequenceOpen: ✓ Active sequence found via QE: ' + qeSeq.name);
   104→                    return true;
   105→                }
   106→            } catch (qeErr) {
   107→                // Silent fallback failure - not critical
   108→            }
   109→        }
   110→
   111→        $.writeln('[SPLICE] checkSequenceOpen: ✗ No active sequence found');
   112→        return false;
   113→    } catch (e) {
   114→        $.writeln('[SPLICE] checkSequenceOpen: ERROR - ' + e.message);
   115→        return false;
   116→    }
   117→}
