   456→// ============================================================================
   457→// OPTIONS TOGGLE
   458→// ============================================================================
   459→function initOptionsToggle() {
   460→    debugLog('initOptionsToggle - toggle:', !!ui.optionsToggle, 'panel:', !!ui.optionsPanel);
   461→    if (ui.optionsToggle && ui.optionsPanel) {
   462→        ui.optionsToggle.addEventListener('click', () => {
   463→            debugLog('Options toggle clicked');
   464→            // Check current state BEFORE toggling
   465→            const wasCollapsed = ui.optionsPanel.classList.contains('collapsed');
   466→            ui.optionsPanel.classList.toggle('collapsed');
   467→            // If it was collapsed, it's now expanded (show 'expanded' class)
   468→            ui.optionsToggle.classList.toggle('expanded', wasCollapsed);
   469→            saveSettings({ expandedOptions: wasCollapsed });
   470→        });
   471→
   472→        // Restore state
   473→        const settings = getSettings();
   474→        if (!settings.expandedOptions) {
   475→            ui.optionsPanel.classList.add('collapsed');
   476→            ui.optionsToggle.classList.remove('expanded');
   477→        } else {
   478→            ui.optionsToggle.classList.add('expanded');
   479→            ui.optionsPanel.classList.remove('collapsed');
   480→        }
   481→    }
   482→}
   483→
   484→// ============================================================================
   485→// TEXT EDITOR TOGGLE
   486→// ============================================================================
   487→function initTextEditorToggle() {
   488→    const toggle = document.getElementById('textEditorToggle');
   489→    const panel = document.getElementById('text-editor-panel');
   490→    debugLog('initTextEditorToggle - toggle:', !!toggle, 'panel:', !!panel);
   491→
   492→    if (toggle && panel) {
   493→        toggle.addEventListener('click', (e) => {
   494→            e.preventDefault();
   495→            e.stopPropagation();
   496→            debugLog('Text Editor toggle clicked');
