   665→function applyPreset(presetId) {
   666→    const preset = PRESETS[presetId];
   667→    if (!preset) return;
   668→
   669→    // Custom preset - keep current settings
   670→    if (presetId === 'custom' || !preset.settings) {
   671→        saveSettings({ activePreset: 'custom' });
   672→        setStatus('Using custom settings');
   673→        return;
   674→    }
   675→
   676→    const settings = preset.settings;
   677→
   678→    // Apply to UI
   679→    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   680→        ui.sensitivitySlider.value = settings.sensitivity;
   681→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   682→    }
   683→
   684→    if (ui.enableJCut) {
   685→        ui.enableJCut.checked = settings.enableJCut || false;
   686→        if (ui.jcutSettings) {
   687→            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   688→        }
   689→    }
   690→
   691→    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   692→        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   693→        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   694→    }
   695→
   696→    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   697→        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   698→        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   699→    }
   700→
   701→    // Save merged settings
   702→    saveSettings({
   703→        activePreset: presetId,
   704→        sensitivity: settings.sensitivity,
   705→        threshold: settings.threshold,
   706→        minSilenceLength: settings.minSilenceLength,
   707→        paddingStart: settings.paddingStart,
   708→        paddingEnd: settings.paddingEnd,
   709→        enableTakesDetection: settings.enableTakesDetection,
   710→        enableJCut: settings.enableJCut || false,
   711→        jcutLeadIn: settings.jCutLeadIn || 0.3,
   712→        jcutLeadOut: settings.jCutLeadOut || 0.2
   713→    });
   714→
   715→    setStatus(`Applied ${preset.name} preset`);
   716→}
