   768→
   769→async function runDetection() {
   770→    if (isOperationInProgress) return;
   771→
   772→    // Check sequence with enhanced error messaging
   773→    const hasSequence = await checkSequenceOpen();
   774→    if (!hasSequence) {
   775→        // Check if there was a communication error vs just no sequence
   776→        const lastError = window.spliceState.lastSequenceCheckError;
   777→        if (lastError && (lastError.includes('CEP') || lastError.includes('communication') || lastError.includes('evalScript'))) {
   778→            setStatus('Premiere Pro connection issue - try reopening panel', true);
   779→            console.error('[SPLICE] runDetection: JSX communication failure:', lastError);
   780→        } else {
   781→            setStatus('Please open a sequence in the Timeline first', true);
   782→            debugLog('runDetection: No active sequence in Timeline');
   783→        }
   784→        // Clear the error for next check
   785→        window.spliceState.lastSequenceCheckError = null;
   786→        return;
   787→    }
   788→
   789→    // Check online
   790→    if (!isOnline()) {
   791→        setStatus('Offline - Check your connection', true);
   792→        return;
   793→    }
   794→
   795→    isOperationInProgress = true;
   796→    ui.goBtn.disabled = true;
   797→    showProgress('Detecting silences...');
   798→
   799→    try {
   800→        const settings = getSettings();
   801→
   802→        // Get sequence info
   803→        const seqInfo = await jsx.call('getActiveSequence');
   804→        debugLog('Sequence info:', seqInfo);
   805→
   806→        if (!seqInfo || seqInfo.error) {
   807→            console.warn('[SPLICE] runDetection: getActiveSequence failed despite checkSequenceOpen returning true');
   808→            setStatus('Could not access active sequence info', true);
   809→            ui.goBtn.disabled = false;
   810→            isOperationInProgress = false;
   811→            hideProgress();
   812→            return;
   813→        }
   814→
   815→        // Store sequence info for later use
   816→        currentSequenceInfo = seqInfo;
   817→        window.spliceState.activeSequence = seqInfo;
   818→        if (seqInfo && seqInfo.videoPath) {
   819→            window.spliceState.currentVideoPath = seqInfo.videoPath;
   820→        }
   821→
   822→        // Export audio via JSX for silence detection
   823→        updateProgress(10, 'Exporting audio...');
   824→
   825→        let audioFilePath = null;
   826→        try {
   827→            // FIX: CEP-CRIT-010 - Main.js Export Timeout
   828→            // Use LONG timeout (120s) for export operations
   829→            const exportResult = await jsx.callWithTimeout(
   830→                'exportSequenceAudioForAnalysis',
   831→                [],
   832→                JSX_TIMEOUTS.LONG
   833→            );
   834→            if (exportResult && exportResult.success && exportResult.outputPath) {
   835→                audioFilePath = exportResult.outputPath;
   836→                debugLog('Audio exported to:', audioFilePath);
   837→            } else if (exportResult && exportResult.error) {
   838→                throw new Error(exportResult.error);
   839→            }
   840→        } catch (exportError) {
   841→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   842→            // Try to get audio from first clip in timeline
   843→            try {
   844→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   845→                if (clipInfo && clipInfo.path) {
   846→                    audioFilePath = clipInfo.path;
   847→                    debugLog('Using clip audio path:', audioFilePath);
   848→                }
   849→            } catch (clipError) {
   850→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   851→            }
   852→        }
   853→
   854→        if (!audioFilePath) {
   855→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   856→        }
   857→
   858→        // Store audio path globally for other modules
   859→        currentAudioPath = audioFilePath;
   860→        window.spliceState.audioPath = audioFilePath;
   861→
   862→        // Call backend for silence detection with audio file path
   863→        updateProgress(30, 'Detecting silences...');
   864→        const silenceResponse = await fetchWithTimeout(
   865→            `${getBackendUrl()}/silences-rms`,
   866→            {
   867→                method: 'POST',
   868→                headers: getAuthHeaders(),
   869→                body: JSON.stringify({
   870→                    wavPath: audioFilePath,
   871→                    sensitivity: settings.sensitivity,
   872→                    minSilenceLength: settings.minSilenceLength || 0.5
   873→                })
   874→            }
   875→        );
   876→
   877→        if (!silenceResponse.ok) {
   878→            throw new Error(await parseErrorResponse(silenceResponse));
   879→        }
   880→
   881→        const silenceData = await silenceResponse.json();
   882→        previewSilences = silenceData.silences || [];
   883→
   884→        // Detect takes if enabled
   885→        if (settings.enableTakesDetection) {
   886→            updateProgress(60, 'Detecting takes...');
   887→            try {
   888→                const analyzeResponse = await fetchWithTimeout(
   889→                    `${getBackendUrl()}/analyze`,
   890→                    {
   891→                        method: 'POST',
   892→                        headers: getAuthHeaders(),
   893→                        body: JSON.stringify({
   894→                            wavPath: audioFilePath,
   895→                            detectTakes: true
   896→                        })
   897→                    }
   898→                );
   899→
   900→                if (analyzeResponse.ok) {
   901→                    const analyzeData = await analyzeResponse.json();
   902→                    previewTakes = analyzeData.takes || [];
   903→
   904→                    // Store transcript for captions/music modules
   905→                    if (analyzeData.transcript) {
   906→                        currentTranscript = analyzeData.transcript;
   907→                        window.currentTranscript = analyzeData.transcript;
   908→                        window.spliceState.lastTranscript = analyzeData.transcript;
   909→                    }
   910→                } else {
   911→                    // Handle specific error codes
   912→                    const errorMsg = await parseErrorResponse(analyzeResponse);
   913→                    if (analyzeResponse.status === 402) {
   914→                        console.warn('[SPLICE] Takes detection skipped: insufficient credits');
   915→                        setStatus('Takes detection skipped: insufficient credits', true);
   916→                    } else if (analyzeResponse.status === 401) {
   917→                        console.warn('[SPLICE] Takes detection skipped: authentication required');
   918→                    } else {
   919→                        console.warn('[SPLICE] Takes detection failed:', errorMsg);
   920→                    }
   921→                }
   922→            } catch (e) {
   923→                console.warn('[SPLICE] Takes detection failed:', e.message);
   924→            }
   925→        }
   926→
   927→        // Detect profanity if enabled
   928→        if (settings.enableProfanity) {
   929→            updateProgress(65, 'Detecting profanity...');
   930→            try {
   931→                const profanityResponse = await fetchWithTimeout(
   932→                    `${getBackendUrl()}/profanity`,
   933→                    {
   934→                        method: 'POST',
   935→                        headers: getAuthHeaders(),
   936→                        body: JSON.stringify({
   937→                            wavPath: audioFilePath,
   938→                            language: settings.profanityLanguage || 'en',
   939→                            bleepType: settings.bleepType || 'standard'
   940→                        })
   941→                    }
   942→                );
   943→
   944→                if (profanityResponse.ok) {
   945→                    const profanityData = await profanityResponse.json();
   946→                    // Backend returns 'words' array, not 'instances'
   947→                    currentProfanity = profanityData.words || profanityData.instances || [];
   948→                    if (currentProfanity.length > 0) {
   949→                        debugLog(`Found ${currentProfanity.length} profanity instances`);
   950→                    }
   951→                } else {
   952→                    console.warn('[SPLICE] Profanity detection failed:', await parseErrorResponse(profanityResponse));
   953→                }
   954→            } catch (e) {
   955→                console.warn('[SPLICE] Profanity detection failed:', e.message);
   956→            }
   957→        }
   958→
   959→        // Detect chapters if enabled
   960→        if (settings.enableChapters) {
   961→            updateProgress(80, 'Detecting chapters...');
   962→            try {
   963→                const chapterResponse = await fetchWithTimeout(
   964→                    `${getBackendUrl()}/chapters`,
   965→                    {
   966→                        method: 'POST',
   967→                        headers: getAuthHeaders(),
   968→                        body: JSON.stringify({
   969→                            wavPath: audioFilePath,
   970→                            transcript: currentTranscript,
   971→                            maxChapters: settings.maxChapters || 10,
   972→                            minChapterLength: settings.minChapterLength || 60
   973→                        })
   974→                    }
   975→                );
   976→
   977→                if (chapterResponse.ok) {
   978→                    const chapterData = await chapterResponse.json();
   979→                    currentChapters = chapterData.chapters || [];
   980→                } else {
   981→                    const errorMsg = await parseErrorResponse(chapterResponse);
   982→                    console.warn('[SPLICE] Chapter detection failed:', errorMsg);
   983→                    // Don't block workflow, just notify user
   984→                    setStatus('Chapter detection skipped: ' + errorMsg, true);
   985→                }
   986→            } catch (e) {
   987→                console.warn('[SPLICE] Chapter detection failed:', e);
   988→                setStatus('Chapter detection failed - continuing without chapters', true);
   989→            }
   990→        }
   991→
   992→        // Detect zoom points if enabled
   993→        if (settings.enableZoom) {
   994→            updateProgress(90, 'Detecting zoom points...');
   995→            try {
   996→                const zoomResponse = await fetchWithTimeout(
   997→                    `${getBackendUrl()}/zoom`,
   998→                    {
   999→                        method: 'POST',
  1000→                        headers: getAuthHeaders(),
  1001→                        body: JSON.stringify({
  1002→                            wavPath: audioFilePath,
  1003→                            transcript: currentTranscript,
  1004→                            frequency: settings.zoomFrequency || 'medium',
  1005→                            preset: settings.zoomPreset || 'medium',
  1006→                            placement: settings.zoomPlacement || 'sentence_start'
  1007→                        })
  1008→                    }
  1009→                );
  1010→
  1011→                if (zoomResponse.ok) {
  1012→                    const zoomData = await zoomResponse.json();
  1013→                    currentZooms = zoomData.zoomPoints || [];
  1014→                } else {
  1015→                    const errorMsg = await parseErrorResponse(zoomResponse);
  1016→                    console.warn('[SPLICE] Zoom detection failed:', errorMsg);
  1017→                    setStatus('Zoom detection skipped: ' + errorMsg, true);
  1018→                }
  1019→            } catch (e) {
  1020→                console.warn('[SPLICE] Zoom detection failed:', e);
  1021→                setStatus('Zoom detection failed - continuing without zoom points', true);
  1022→            }
  1023→        }
  1024→
  1025→        // Store results
  1026→        lastDetectionResult = {
  1027→            silences: previewSilences,
  1028→            takes: previewTakes,
  1029→            chapters: currentChapters,
  1030→            zooms: currentZooms
  1031→        };
  1032→
  1033→        // Show preview
  1034→        hideProgress();
  1035→        showCombinedPreview();
  1036→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
  1037→
  1038→    } catch (error) {
  1039→        console.error('[SPLICE] Detection error:', error);
  1040→        setStatus('Detection failed: ' + error.message, true);
  1041→        hideProgress();
  1042→    } finally {
  1043→        isOperationInProgress = false;
  1044→        ui.goBtn.disabled = false;
  1045→    }
  1046→}
