  1051→function showProgress(message) {
  1052→    if (ui.progressContainer) ui.progressContainer.classList.remove('hidden');
  1053→    if (ui.resultsEmpty) ui.resultsEmpty.classList.add('hidden');
  1054→    if (ui.combinedPreview) ui.combinedPreview.classList.add('hidden');
  1055→    updateProgress(0, message);
