  1067→// ============================================================================
  1068→// COMBINED PREVIEW
  1069→// ============================================================================
  1070→function showCombinedPreview() {
  1071→    if (!ui.combinedPreview) return;
  1072→
  1073→    ui.combinedPreview.classList.remove('hidden');
  1074→    if (ui.resultsEmpty) ui.resultsEmpty.classList.add('hidden');
  1075→
