  1174→async function seekToTime(seconds) {
  1175→    try {
  1176→        // FIX: Removed invalid 'app' check - 'app' is ExtendScript DOM, not available in panel JS
  1177→        // The JSX evalScript will handle the error case if no sequence is open
  1178→
  1179→        // Use JSX to set playhead position
  1180→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
  1181→        const ticks = Math.round(seconds * 254016000000);
  1182→        const result = await jsx.evalScript(`
  1183→            (function() {
  1184→                try {
  1185→                    if (!app.project.activeSequence) {
  1186→                        return JSON.stringify({ error: 'No active sequence' });
  1187→                    }
  1188→                    app.project.activeSequence.setPlayerPosition('${ticks}');
  1189→                    return JSON.stringify({ success: true });
  1190→                } catch (e) {
  1191→                    return JSON.stringify({ error: e.message });
  1192→                }
  1193→            })()
  1194→        `);
  1195→
  1196→        // Check for errors
  1197→        if (result && typeof result === 'string') {
  1198→            try {
  1199→                const parsed = JSON.parse(result);
  1200→                if (parsed.error) {
  1201→                    debugLog('Seek failed:', parsed.error);
  1202→                }
  1203→            } catch (parseErr) {
  1204→                // Result wasn't JSON, ignore
  1205→            }
  1206→        }
  1207→    } catch (e) {
  1208→        console.warn('[SPLICE] Seek failed:', e);
  1209→    }
  1210→}
