  1543→/**
  1544→ * ACCESSIBILITY: Announce message to screen readers via ARIA live region
  1545→ * @param {string} message - Message to announce
  1546→ * @param {string} priority - 'polite' or 'assertive'
  1547→ */
  1548→function announceToScreenReader(message, priority = 'polite') {
  1549→    // Use existing status element or create temporary one
  1550→    const statusEl = document.getElementById('status');
  1551→    if (statusEl && priority === 'polite') {
  1552→        const originalText = statusEl.textContent;
  1553→        statusEl.textContent = message;
  1554→        // Restore after announcement
  1555→        setTimeout(() => {
  1556→            if (statusEl.textContent === message) {
  1557→                statusEl.textContent = originalText;
  1558→            }
  1559→        }, 1000);
  1560→        return;
  1561→    }
  1562→
  1563→    // Create temporary live region for assertive announcements
  1564→    const liveRegion = document.createElement('div');
  1565→    liveRegion.setAttribute('role', 'alert');
  1566→    liveRegion.setAttribute('aria-live', priority);
  1567→    liveRegion.setAttribute('aria-atomic', 'true');
  1568→    liveRegion.className = 'sr-only';
  1569→    liveRegion.style.cssText = 'position:absolute;width:1px;height:1px;padding:0;margin:-1px;overflow:hidden;clip:rect(0,0,0,0);border:0;';
  1570→    liveRegion.textContent = message;
  1571→    document.body.appendChild(liveRegion);
  1572→
  1573→    // Remove after announcement
  1574→    setTimeout(() => liveRegion.remove(), 1000);
  1575→}
