  1718→async function updateCredits() {
  1719→    if (!ui.creditBadge) return;
  1720→
  1721→    const settings = getSettings();
  1722→    if (!settings.customerId) {
  1723→        ui.creditBadge.className = 'credit-badge login';
  1724→        ui.creditBadge.textContent = 'Login';
  1725→        ui.creditBadge.style.display = 'flex';
