     1→/**
     2→ * SPLICE CEP Panel - Compatibility Detection Module
     3→ * v6.0.8
     4→ *
     5→ * Detects runtime environment and provides feature detection utilities.
     6→ * Uses feature detection instead of hardware detection for reliability.
     7→ */
     8→
     9→// ============================================================================
    10→// COMPATIBILITY MODULE
    11→// ============================================================================
    12→
    13→const SpliceCompatibility = (function() {
    14→    'use strict';
    15→
    16→    // Cache for detected environment info
    17→    let _envCache = null;
    18→    let _featuresCache = null;
    19→    let _initialized = false;
    20→
    21→    // ========================================================================
    22→    // ENVIRONMENT DETECTION
    23→    // ========================================================================
    24→
    25→    /**
    26→     * Detect the runtime environment (CEP, macOS version, Premiere Pro version)
    27→     * @returns {Object} Environment information
    28→     */
    29→    function detectEnvironment() {
    30→        if (_envCache) {
    31→            return _envCache;
    32→        }
    33→
    34→        const env = {
    35→            // Timestamp
    36→            detectedAt: new Date().toISOString(),
    37→
    38→            // Platform info
    39→            platform: {
    40→                os: detectOS(),
    41→                osVersion: detectOSVersion(),
    42→                architecture: detectArchitecture(),
    43→                isAppleSilicon: detectAppleSilicon(),
    44→                userAgent: navigator.userAgent || 'unknown'
    45→            },
    46→
    47→            // CEP environment
    48→            cep: {
    49→                isRunning: isCEPEnvironment(),
    50→                version: getCEPVersion(),
    51→                hostApp: null,
    52→                hostVersion: null,
    53→                extensionId: null,
    54→                locale: null
    55→            },
    56→
    57→            // Host application (Premiere Pro)
    58→            host: {
    59→                name: null,
    60→                version: null,
    61→                versionMajor: null,
    62→                versionMinor: null,
    63→                isOffline: false
    64→            },
    65→
    66→            // Browser/Chromium info
    67→            browser: {
    68→                chromiumVersion: getChromiumVersion(),
    69→                v8Version: getV8Version(),
    70→                supportsES6: supportsES6(),
    71→                supportsAsyncAwait: supportsAsyncAwait()
    72→            }
    73→        };
    74→
    75→        // Detect CEP host environment if available
    76→        if (env.cep.isRunning) {
    77→            try {
    78→                const cs = new CSInterface();
    79→                const hostEnv = cs.getHostEnvironment();
    80→
    81→                if (hostEnv) {
    82→                    env.cep.hostApp = hostEnv.appName || null;
    83→                    env.cep.hostVersion = hostEnv.appVersion || null;
    84→                    env.host.name = getHostAppName(hostEnv.appName);
    85→                    env.host.version = hostEnv.appVersion || null;
    86→                    env.host.isOffline = hostEnv.isAppOffline || false;
    87→                    env.cep.locale = hostEnv.appLocale || null;
    88→
    89→                    // Parse version numbers
    90→                    if (hostEnv.appVersion) {
    91→                        const versionParts = hostEnv.appVersion.split('.');
    92→                        env.host.versionMajor = parseInt(versionParts[0]) || 0;
    93→                        env.host.versionMinor = parseInt(versionParts[1]) || 0;
    94→                    }
    95→                }
    96→
    97→                env.cep.extensionId = cs.getExtensionId() || 'com.splice.panel.main';
    98→            } catch (e) {
    99→                debugLog('[Compatibility] Error getting host environment:', e.message);
   100→            }
   101→        }
   102→
   103→        _envCache = env;
   104→        return env;
   105→    }
   106→
   107→    /**
   108→     * Detect operating system
   109→     * @returns {string} OS name
   110→     */
   111→    function detectOS() {
   112→        const ua = navigator.userAgent || '';
   113→        const platform = navigator.platform || '';
   114→
   115→        if (platform.indexOf('Mac') !== -1 || ua.indexOf('Mac') !== -1) {
   116→            return 'macOS';
   117→        } else if (platform.indexOf('Win') !== -1 || ua.indexOf('Windows') !== -1) {
   118→            return 'Windows';
   119→        } else if (platform.indexOf('Linux') !== -1 || ua.indexOf('Linux') !== -1) {
   120→            return 'Linux';
   121→        }
   122→        return 'unknown';
   123→    }
   124→
   125→    /**
   126→     * Detect OS version
   127→     * @returns {string} OS version string
   128→     */
   129→    function detectOSVersion() {
   130→        const ua = navigator.userAgent || '';
   131→
   132→        // macOS version detection
   133→        const macMatch = ua.match(/Mac OS X (\d+[._]\d+[._]?\d*)/);
   134→        if (macMatch) {
   135→            return macMatch[1].replace(/_/g, '.');
   136→        }
   137→
   138→        // Windows version detection
   139→        const winMatch = ua.match(/Windows NT (\d+\.\d+)/);
   140→        if (winMatch) {
   141→            const ntVersion = winMatch[1];
   142→            // Map NT versions to Windows versions
   143→            const ntMap = {
   144→                '10.0': '10/11',
   145→                '6.3': '8.1',
   146→                '6.2': '8',
   147→                '6.1': '7',
   148→                '6.0': 'Vista'
   149→            };
   150→            return ntMap[ntVersion] || ntVersion;
   151→        }
   152→
   153→        return 'unknown';
   154→    }
   155→
   156→    /**
   157→     * Detect CPU architecture
   158→     * Note: This is limited in browser environments
   159→     * @returns {string} Architecture string
   160→     */
   161→    function detectArchitecture() {
   162→        // Check navigator.platform for architecture hints
   163→        const platform = navigator.platform || '';
   164→        const ua = navigator.userAgent || '';
   165→
   166→        // Check for ARM64 indicators
   167→        if (ua.indexOf('arm64') !== -1 || ua.indexOf('ARM64') !== -1) {
   168→            return 'arm64';
   169→        }
   170→
   171→        // macOS on Apple Silicon may not always show arm64 in UA
   172→        // but we can infer from other factors
   173→        if (platform === 'MacIntel') {
   174→            // Modern Safari/Chromium on Apple Silicon may still report 'MacIntel'
   175→            // for compatibility, so we need to check other signals
   176→            return detectAppleSilicon() ? 'arm64' : 'x86_64';
   177→        }
   178→
   179→        if (platform.indexOf('64') !== -1 || ua.indexOf('x86_64') !== -1 || ua.indexOf('x64') !== -1) {
   180→            return 'x86_64';
   181→        }
   182→
   183→        if (platform.indexOf('Win32') !== -1 && ua.indexOf('WOW64') !== -1) {
   184→            return 'x86_64'; // 32-bit browser on 64-bit Windows
   185→        }
   186→
   187→        return 'unknown';
   188→    }
   189→
   190→    /**
   191→     * Detect if running on Apple Silicon (M1/M2/M3/M4)
   192→     * Uses multiple heuristics since direct detection is limited
   193→     * @returns {boolean} True if likely Apple Silicon
   194→     */
   195→    function detectAppleSilicon() {
   196→        // Check for explicit arm64 indicator
   197→        const ua = navigator.userAgent || '';
   198→        if (ua.indexOf('arm64') !== -1 || ua.indexOf('ARM64') !== -1) {
   199→            return true;
   200→        }
   201→
   202→        // macOS-specific checks
   203→        if (detectOS() !== 'macOS') {
   204→            return false;
   205→        }
   206→
   207→        // Method 1: Check for GL_RENDERER containing Apple GPU names
   208→        // (Only works if WebGL is available and exposes this info)
   209→        try {
   210→            const canvas = document.createElement('canvas');
   211→            const gl = canvas.getContext('webgl') || canvas.getContext('experimental-webgl');
   212→            if (gl) {
   213→                const debugInfo = gl.getExtension('WEBGL_debug_renderer_info');
   214→                if (debugInfo) {
   215→                    const renderer = gl.getParameter(debugInfo.UNMASKED_RENDERER_WEBGL);
   216→                    // Apple Silicon GPUs contain "Apple" in the renderer name
   217→                    if (renderer && renderer.indexOf('Apple') !== -1) {
   218→                        // Check for M-series chip indicators
   219→                        // Apple Silicon integrated GPUs are named like "Apple M1", "Apple M2", etc.
   220→                        if (/Apple M\d/.test(renderer)) {
   221→                            return true;
   222→                        }
   223→                    }
   224→                }
   225→            }
   226→        } catch (e) {
   227→            // WebGL detection failed, continue with other methods
   228→        }
   229→
   230→        // Method 2: Check macOS version (Apple Silicon requires macOS 11+)
   231→        const osVersion = detectOSVersion();
   232→        const majorVersion = parseInt(osVersion.split('.')[0]) || 0;
   233→
   234→        // macOS 11 (Big Sur) and later are required for Apple Silicon
   235→        // However, this is not definitive - Intel Macs can also run Big Sur+
   236→        // So this is just a prerequisite check
   237→        if (majorVersion < 11) {
   238→            return false;
   239→        }
   240→
   241→        // Method 3: Performance heuristics (Apple Silicon has specific characteristics)
   242→        // This is a soft indicator and not definitive
   243→        // Apple Silicon typically has high core counts and specific timing patterns
   244→        // However, we avoid this as it's unreliable
   245→
   246→        // Default to unknown/false for Intel detection
   247→        // We can't definitively detect Apple Silicon without system access
   248→        return false;
   249→    }
   250→
   251→    /**
   252→     * Check if running in CEP environment
   253→     * @returns {boolean}
   254→     */
   255→    function isCEPEnvironment() {
   256→        return typeof window.__adobe_cep__ !== 'undefined' && window.__adobe_cep__ !== null;
   257→    }
   258→
   259→    /**
   260→     * Get CEP version
   261→     * @returns {string|null}
   262→     */
   263→    function getCEPVersion() {
   264→        if (!isCEPEnvironment()) {
   265→            return null;
   266→        }
   267→
   268→        try {
   269→            // CEP version is typically exposed through various means
   270→            if (window.__adobe_cep__.getHostEnvironment) {
   271→                const env = JSON.parse(window.__adobe_cep__.getHostEnvironment());
   272→                if (env && env.CEPVersion) {
   273→                    return env.CEPVersion;
   274→                }
   275→            }
   276→        } catch (e) {
   277→            // Ignore errors
   278→        }
   279→
   280→        // Default to CEP 11 based on manifest
   281→        return '11.0';
   282→    }
   283→
   284→    /**
   285→     * Get human-readable host application name
   286→     * @param {string} appCode - Adobe app code (e.g., 'PPRO')
   287→     * @returns {string}
   288→     */
   289→    function getHostAppName(appCode) {
   290→        const appNames = {
   291→            'PPRO': 'Adobe Premiere Pro',
   292→            'AEFT': 'Adobe After Effects',
   293→            'AUDT': 'Adobe Audition',
   294→            'PHXS': 'Adobe Photoshop',
   295→            'ILST': 'Adobe Illustrator',
   296→            'IDSN': 'Adobe InDesign'
   297→        };
   298→        return appNames[appCode] || appCode || 'Unknown';
   299→    }
   300→
   301→    /**
   302→     * Get Chromium version from user agent
   303→     * @returns {string|null}
   304→     */
   305→    function getChromiumVersion() {
   306→        const match = (navigator.userAgent || '').match(/Chrome\/(\d+\.\d+\.\d+\.\d+)/);
   307→        return match ? match[1] : null;
   308→    }
   309→
   310→    /**
   311→     * Get V8 JavaScript engine version
   312→     * @returns {string|null}
   313→     */
   314→    function getV8Version() {
   315→        // V8 version detection is limited in browsers
   316→        // CEP uses Chromium Embedded Framework (CEF)
   317→        const chromiumVersion = getChromiumVersion();
   318→        if (chromiumVersion) {
   319→            // V8 version roughly correlates with Chromium version
   320→            return 'CEF/' + chromiumVersion;
   321→        }
   322→        return null;
   323→    }
   324→
   325→    /**
   326→     * Check ES6 support
   327→     * @returns {boolean}
   328→     */
   329→    function supportsES6() {
   330→        try {
   331→            // Test for arrow functions, const, let, template literals
   332→            new Function('const x = 1; let y = 2; const f = () => `${x}${y}`; return f();')();
   333→            return true;
   334→        } catch (e) {
   335→            return false;
   336→        }
   337→    }
   338→
   339→    /**
   340→     * Check async/await support
   341→     * @returns {boolean}
   342→     */
   343→    function supportsAsyncAwait() {
   344→        try {
   345→            new Function('async function f() { await Promise.resolve(); }')();
   346→            return true;
   347→        } catch (e) {
   348→            return false;
   349→        }
   350→    }
   351→
   352→    // ========================================================================
   353→    // FEATURE DETECTION
   354→    // ========================================================================
   355→
   356→    /**
   357→     * Detect available features and capabilities
   358→     * Uses feature detection instead of hardware detection for reliability
   359→     * @returns {Object} Feature availability
   360→     */
   361→    function detectFeatures() {
   362→        if (_featuresCache) {
   363→            return _featuresCache;
   364→        }
   365→
   366→        const features = {
   367→            // Web APIs
   368→            webAPIs: {
   369→                fetch: typeof fetch === 'function',
   370→                promises: typeof Promise === 'function',
   371→                webCrypto: typeof crypto !== 'undefined' && typeof crypto.subtle !== 'undefined',
   372→                localStorage: hasLocalStorage(),
   373→                sessionStorage: hasSessionStorage(),
   374→                indexedDB: typeof indexedDB !== 'undefined',
   375→                webWorkers: typeof Worker === 'function',
   376→                webGL: hasWebGL(),
   377→                webGL2: hasWebGL2(),
   378→                canvas: hasCanvas(),
   379→                audioContext: hasAudioContext(),
   380→                mediaDevices: typeof navigator.mediaDevices !== 'undefined',
   381→                clipboard: typeof navigator.clipboard !== 'undefined',
   382→                notifications: typeof Notification !== 'undefined',
   383→                serviceWorkers: 'serviceWorker' in navigator,
   384→                webSockets: typeof WebSocket === 'function'
   385→            },
   386→
   387→            // JavaScript features
   388→            javascript: {
   389→                es6: supportsES6(),
   390→                asyncAwait: supportsAsyncAwait(),
   391→                generators: supportsGenerators(),
   392→                symbols: typeof Symbol === 'function',
   393→                maps: typeof Map === 'function',
   394→                sets: typeof Set === 'function',
   395→                weakMaps: typeof WeakMap === 'function',
   396→                proxies: typeof Proxy === 'function',
   397→                reflect: typeof Reflect !== 'undefined',
   398→                bigInt: typeof BigInt === 'function'
   399→            },
   400→
   401→            // CEP-specific features
   402→            cep: {
   403→                evalScript: hasCEPEvalScript(),
   404→                fileSystem: hasCEPFileSystem(),
   405→                nodeJS: hasCEPNodeJS(),
   406→                vulcanMessage: hasCEPVulcanMessage(),
   407→                flyoutMenu: hasCEPFlyoutMenu()
   408→            },
   409→
   410→            // Premiere Pro features (detected via JSX)
   411→            premiere: {
   412→                detected: false,
   413→                activeSequence: false,
   414→                markers: false,
   415→                exportMedia: false,
   416→                qeDom: false
   417→            }
   418→        };
   419→
   420→        _featuresCache = features;
   421→        return features;
   422→    }
   423→
   424→    /**
   425→     * Check localStorage availability
   426→     * @returns {boolean}
   427→     */
   428→    function hasLocalStorage() {
   429→        try {
   430→            const test = '__splice_test__';
   431→            localStorage.setItem(test, test);
   432→            localStorage.removeItem(test);
   433→            return true;
   434→        } catch (e) {
   435→            return false;
   436→        }
   437→    }
   438→
   439→    /**
   440→     * Check sessionStorage availability
   441→     * @returns {boolean}
   442→     */
   443→    function hasSessionStorage() {
   444→        try {
   445→            const test = '__splice_test__';
   446→            sessionStorage.setItem(test, test);
   447→            sessionStorage.removeItem(test);
   448→            return true;
   449→        } catch (e) {
   450→            return false;
   451→        }
   452→    }
   453→
   454→    /**
   455→     * Check WebGL availability
   456→     * @returns {boolean}
   457→     */
   458→    function hasWebGL() {
   459→        try {
   460→            const canvas = document.createElement('canvas');
   461→            return !!(canvas.getContext('webgl') || canvas.getContext('experimental-webgl'));
   462→        } catch (e) {
   463→            return false;
   464→        }
   465→    }
   466→
   467→    /**
   468→     * Check WebGL 2 availability
   469→     * @returns {boolean}
   470→     */
   471→    function hasWebGL2() {
   472→        try {
   473→            const canvas = document.createElement('canvas');
   474→            return !!canvas.getContext('webgl2');
   475→        } catch (e) {
   476→            return false;
   477→        }
   478→    }
   479→
   480→    /**
   481→     * Check canvas availability
   482→     * @returns {boolean}
   483→     */
   484→    function hasCanvas() {
   485→        try {
   486→            const canvas = document.createElement('canvas');
   487→            return !!canvas.getContext('2d');
   488→        } catch (e) {
   489→            return false;
   490→        }
   491→    }
   492→
   493→    /**
   494→     * Check AudioContext availability
   495→     * @returns {boolean}
   496→     */
   497→    function hasAudioContext() {
   498→        return typeof AudioContext !== 'undefined' || typeof webkitAudioContext !== 'undefined';
   499→    }
   500→
   501→    /**
   502→     * Check generator function support
   503→     * @returns {boolean}
   504→     */
   505→    function supportsGenerators() {
   506→        try {
   507→            new Function('function* f() { yield 1; }')();
   508→            return true;
   509→        } catch (e) {
   510→            return false;
   511→        }
   512→    }
   513→
   514→    /**
   515→     * Check CEP evalScript availability
   516→     * @returns {boolean}
   517→     */
   518→    function hasCEPEvalScript() {
   519→        return isCEPEnvironment() && typeof window.__adobe_cep__.evalScript === 'function';
   520→    }
   521→
   522→    /**
   523→     * Check CEP file system access
   524→     * @returns {boolean}
   525→     */
   526→    function hasCEPFileSystem() {
   527→        return isCEPEnvironment() && typeof window.cep !== 'undefined' &&
   528→               typeof window.cep.fs !== 'undefined';
   529→    }
   530→
   531→    /**
   532→     * Check CEP Node.js availability
   533→     * @returns {boolean}
   534→     */
   535→    function hasCEPNodeJS() {
   536→        return typeof process !== 'undefined' && typeof require === 'function';
   537→    }
   538→
   539→    /**
   540→     * Check CEP Vulcan messaging
   541→     * @returns {boolean}
   542→     */
   543→    function hasCEPVulcanMessage() {
   544→        return typeof VulcanInterface !== 'undefined';
   545→    }
   546→
   547→    /**
   548→     * Check CEP flyout menu
   549→     * @returns {boolean}
   550→     */
   551→    function hasCEPFlyoutMenu() {
   552→        if (!isCEPEnvironment()) return false;
   553→        try {
   554→            const cs = new CSInterface();
   555→            return typeof cs.setPanelFlyoutMenu === 'function';
   556→        } catch (e) {
   557→            return false;
   558→        }
   559→    }
   560→
   561→    // ========================================================================
   562→    // PREMIERE PRO FEATURE DETECTION (ASYNC)
   563→    // ========================================================================
   564→
   565→    /**
   566→     * Detect Premiere Pro features via JSX
   567→     * Must be called after JSX bridge is initialized
   568→     * @returns {Promise<Object>} Premiere features
   569→     */
   570→    async function detectPremiereFeatures() {
   571→        if (!window.jsx || !window.jsx.cs) {
   572→            debugLog('[Compatibility] JSX bridge not available for Premiere detection');
   573→            return _featuresCache?.premiere || {};
   574→        }
   575→
   576→        const features = {
   577→            detected: true,
   578→            activeSequence: false,
   579→            markers: false,
   580→            exportMedia: false,
   581→            qeDom: false
   582→        };
   583→
   584→        try {
   585→            // Check for active sequence support
   586→            const seqResult = await window.jsx.evalScript('typeof app.project.activeSequence !== "undefined"');
   587→            features.activeSequence = seqResult === 'true';
   588→        } catch (e) {
   589→            debugLog('[Compatibility] Failed to detect activeSequence:', e.message);
   590→        }
   591→
   592→        try {
   593→            // Check for markers support
   594→            const markersResult = await window.jsx.evalScript(
   595→                '(function() { try { return typeof app.project.activeSequence.markers !== "undefined"; } catch(e) { return false; } })()'
   596→            );
   597→            features.markers = markersResult === 'true';
   598→        } catch (e) {
   599→            debugLog('[Compatibility] Failed to detect markers:', e.message);
   600→        }
   601→
   602→        try {
   603→            // Check for QE DOM availability
   604→            const qeResult = await window.jsx.evalScript('typeof qe !== "undefined"');
   605→            features.qeDom = qeResult === 'true';
   606→        } catch (e) {
   607→            debugLog('[Compatibility] Failed to detect QE DOM:', e.message);
   608→        }
   609→
   610→        try {
   611→            // Check for export media encoder
   612→            const exportResult = await window.jsx.evalScript('typeof app.encoder !== "undefined"');
   613→            features.exportMedia = exportResult === 'true';
   614→        } catch (e) {
   615→            debugLog('[Compatibility] Failed to detect export media:', e.message);
   616→        }
   617→
   618→        // Update cache
   619→        if (_featuresCache) {
   620→            _featuresCache.premiere = features;
   621→        }
   622→
   623→        return features;
   624→    }
   625→
   626→    // ========================================================================
   627→    // COMPATIBILITY CHECKS
   628→    // ========================================================================
   629→
   630→    /**
   631→     * Check if environment meets minimum requirements
   632→     * @returns {Object} Compatibility status with details
   633→     */
   634→    function checkCompatibility() {
   635→        const env = detectEnvironment();
   636→        const features = detectFeatures();
   637→
   638→        const result = {
   639→            compatible: true,
   640→            warnings: [],
   641→            errors: [],
   642→            recommendations: []
   643→        };
   644→
   645→        // Check CEP environment
   646→        if (!env.cep.isRunning) {
   647→            result.warnings.push({
   648→                code: 'NO_CEP',
   649→                message: 'Not running in CEP environment. Some features will be unavailable.',
   650→                severity: 'warning'
   651→            });
   652→        }
   653→
   654→        // Check Premiere Pro version
   655→        if (env.host.versionMajor && env.host.versionMajor < 22) {
   656→            result.errors.push({
   657→                code: 'PPRO_VERSION_LOW',
   658→                message: `Premiere Pro version ${env.host.version} is not supported. Minimum required: 22.0 (2022)`,
   659→                severity: 'error'
   660→            });
   661→            result.compatible = false;
   662→        }
   663→
   664→        // Check for required web APIs
   665→        if (!features.webAPIs.fetch) {
   666→            result.errors.push({
   667→                code: 'NO_FETCH',
   668→                message: 'Fetch API not available. Network operations will fail.',
   669→                severity: 'error'
   670→            });
   671→            result.compatible = false;
   672→        }
   673→
   674→        if (!features.webAPIs.promises) {
   675→            result.errors.push({
   676→                code: 'NO_PROMISES',
   677→                message: 'Promise API not available. Async operations will fail.',
   678→                severity: 'error'
   679→            });
   680→            result.compatible = false;
   681→        }
   682→
   683→        if (!features.webAPIs.localStorage) {
   684→            result.warnings.push({
   685→                code: 'NO_LOCALSTORAGE',
   686→                message: 'localStorage not available. Settings will not persist.',
   687→                severity: 'warning'
   688→            });
   689→        }
   690→
   691→        // Check JavaScript features
   692→        if (!features.javascript.asyncAwait) {
   693→            result.warnings.push({
   694→                code: 'NO_ASYNC_AWAIT',
   695→                message: 'async/await not supported. Some features may not work.',
   696→                severity: 'warning'
   697→            });
   698→        }
   699→
   700→        // Architecture-specific recommendations
   701→        if (env.platform.isAppleSilicon) {
   702→            result.recommendations.push({
   703→                code: 'APPLE_SILICON',
   704→                message: 'Apple Silicon detected. Ensure Premiere Pro is running natively (not via Rosetta) for best performance.'
   705→            });
   706→        }
   707→
   708→        // CEP feature warnings
   709→        if (!features.cep.evalScript) {
   710→            result.warnings.push({
   711→                code: 'NO_EVALSCRIPT',
   712→                message: 'CEP evalScript not available. Cannot communicate with Premiere Pro.',
   713→                severity: 'warning'
   714→            });
   715→        }
   716→
   717→        return result;
   718→    }
   719→
   720→    // ========================================================================
   721→    // LOGGING AND INITIALIZATION
   722→    // ========================================================================
   723→
   724→    /**
   725→     * Log environment information to console
   726→     * Called during extension initialization
   727→     */
   728→    function logEnvironmentInfo() {
   729→        const env = detectEnvironment();
   730→        const features = detectFeatures();
   731→
   732→        console.log('[SPLICE] ==================== Environment Info ====================');
   733→        console.log('[SPLICE] Platform:', env.platform.os, env.platform.osVersion);
   734→        console.log('[SPLICE] Architecture:', env.platform.architecture);
   735→        console.log('[SPLICE] Apple Silicon:', env.platform.isAppleSilicon ? 'Yes' : 'No');
   736→        console.log('[SPLICE] CEP Runtime:', env.cep.isRunning ? 'Yes' : 'No (Browser Mode)');
   737→
   738→        if (env.cep.isRunning) {
   739→            console.log('[SPLICE] CEP Version:', env.cep.version);
   740→            console.log('[SPLICE] Host App:', env.host.name, env.host.version);
   741→            console.log('[SPLICE] Extension ID:', env.cep.extensionId);
   742→            console.log('[SPLICE] Locale:', env.cep.locale);
   743→        }
   744→
   745→        console.log('[SPLICE] Chromium:', env.browser.chromiumVersion || 'N/A');
   746→        console.log('[SPLICE] ES6 Support:', features.javascript.es6 ? 'Yes' : 'No');
   747→        console.log('[SPLICE] Async/Await:', features.javascript.asyncAwait ? 'Yes' : 'No');
   748→        console.log('[SPLICE] Web Crypto:', features.webAPIs.webCrypto ? 'Yes' : 'No');
   749→        console.log('[SPLICE] localStorage:', features.webAPIs.localStorage ? 'Yes' : 'No');
   750→        console.log('[SPLICE] ===============================================================');
   751→    }
   752→
   753→    /**
   754→     * Initialize the compatibility module
   755→     * Should be called early in the extension startup
   756→     * @returns {Object} Initialization result
   757→     */
   758→    function init() {
   759→        if (_initialized) {
   760→            return { success: true, cached: true };
   761→        }
   762→
   763→        try {
   764→            // Detect environment and features
   765→            const env = detectEnvironment();
   766→            const features = detectFeatures();
   767→            const compatibility = checkCompatibility();
   768→
   769→            // Log environment info
   770→            logEnvironmentInfo();
   771→
   772→            // Log compatibility issues
   773→            if (compatibility.errors.length > 0) {
   774→                console.error('[SPLICE] Compatibility errors detected:');
   775→                compatibility.errors.forEach(err => {
   776→                    console.error(`[SPLICE]   - ${err.code}: ${err.message}`);
   777→                });
   778→            }
   779→
   780→            if (compatibility.warnings.length > 0) {
   781→                console.warn('[SPLICE] Compatibility warnings:');
   782→                compatibility.warnings.forEach(warn => {
   783→                    console.warn(`[SPLICE]   - ${warn.code}: ${warn.message}`);
   784→                });
   785→            }
   786→
   787→            if (compatibility.recommendations.length > 0) {
   788→                console.log('[SPLICE] Recommendations:');
   789→                compatibility.recommendations.forEach(rec => {
   790→                    console.log(`[SPLICE]   - ${rec.message}`);
   791→                });
   792→            }
   793→
   794→            _initialized = true;
   795→
   796→            return {
   797→                success: true,
   798→                compatible: compatibility.compatible,
   799→                environment: env,
   800→                features: features,
   801→                compatibility: compatibility
   802→            };
   803→        } catch (e) {
   804→            console.error('[SPLICE] Compatibility detection failed:', e.message);
   805→            return {
   806→                success: false,
   807→                error: e.message,
   808→                compatible: true // Assume compatible if detection fails
   809→            };
   810→        }
   811→    }
   812→
   813→    // ========================================================================
   814→    // UTILITY FUNCTIONS
   815→    // ========================================================================
   816→
   817→    /**
   818→     * Get a summary string for diagnostics
   819→     * @returns {string}
   820→     */
   821→    function getSummary() {
   822→        const env = detectEnvironment();
   823→        const parts = [];
   824→
   825→        parts.push(`OS: ${env.platform.os} ${env.platform.osVersion}`);
   826→        parts.push(`Arch: ${env.platform.architecture}`);
   827→
   828→        if (env.platform.isAppleSilicon) {
   829→            parts.push('(Apple Silicon)');
   830→        }
   831→
   832→        if (env.cep.isRunning) {
   833→            parts.push(`CEP: ${env.cep.version}`);
   834→            parts.push(`Host: ${env.host.name} ${env.host.version}`);
   835→        } else {
   836→            parts.push('Mode: Browser');
   837→        }
   838→
   839→        return parts.join(' | ');
   840→    }
   841→
   842→    /**
   843→     * Check if a specific feature is available
   844→     * @param {string} category - Feature category (webAPIs, javascript, cep)
   845→     * @param {string} feature - Feature name
   846→     * @returns {boolean}
   847→     */
   848→    function hasFeature(category, feature) {
   849→        const features = detectFeatures();
   850→        return features[category] && features[category][feature] === true;
   851→    }
   852→
   853→    /**
   854→     * Get Premiere Pro version as comparable number
   855→     * @returns {number} Version number (e.g., 25.1 -> 2501)
   856→     */
   857→    function getPremiereVersionNumber() {
   858→        const env = detectEnvironment();
   859→        if (!env.host.versionMajor) return 0;
   860→        return (env.host.versionMajor * 100) + (env.host.versionMinor || 0);
   861→    }
   862→
   863→    /**
   864→     * Check if running on minimum Premiere Pro version
   865→     * @param {number} majorVersion - Minimum major version
   866→     * @param {number} minorVersion - Minimum minor version (optional)
   867→     * @returns {boolean}
   868→     */
   869→    function isPremiereVersionAtLeast(majorVersion, minorVersion = 0) {
   870→        const required = (majorVersion * 100) + minorVersion;
   871→        return getPremiereVersionNumber() >= required;
   872→    }
   873→
   874→    // ========================================================================
   875→    // PUBLIC API
   876→    // ========================================================================
   877→
   878→    return {
   879→        // Initialization
   880→        init: init,
   881→
   882→        // Environment detection
   883→        detectEnvironment: detectEnvironment,
   884→        detectFeatures: detectFeatures,
   885→        detectPremiereFeatures: detectPremiereFeatures,
   886→
   887→        // Compatibility checking
   888→        checkCompatibility: checkCompatibility,
   889→
   890→        // Utility functions
   891→        getSummary: getSummary,
   892→        hasFeature: hasFeature,
   893→        getPremiereVersionNumber: getPremiereVersionNumber,
   894→        isPremiereVersionAtLeast: isPremiereVersionAtLeast,
   895→
   896→        // Quick checks
   897→        isCEPEnvironment: isCEPEnvironment,
   898→        isAppleSilicon: function() { return detectEnvironment().platform.isAppleSilicon; },
   899→        getOS: function() { return detectEnvironment().platform.os; },
   900→        getOSVersion: function() { return detectEnvironment().platform.osVersion; },
   901→        getArchitecture: function() { return detectEnvironment().platform.architecture; },
   902→        getHostVersion: function() { return detectEnvironment().host.version; },
   903→
   904→        // Logging
   905→        logEnvironmentInfo: logEnvironmentInfo
   906→    };
   907→})();
   908→
   909→// ============================================================================
   910→// AUTO-INITIALIZATION
   911→// ============================================================================
   912→
   913→// Initialize compatibility detection on DOMContentLoaded
   914→document.addEventListener('DOMContentLoaded', function() {
   915→    const result = SpliceCompatibility.init();
   916→
   917→    if (result.success) {
   918→        debugLog('[Compatibility] Module initialized successfully');
   919→
   920→        // Store in window for debugging
   921→        window.spliceCompatibility = SpliceCompatibility;
   922→
   923→        // Also make the summary available
   924→        window.spliceEnvironmentSummary = SpliceCompatibility.getSummary();
   925→    } else {
   926→        console.error('[SPLICE] Compatibility module failed to initialize:', result.error);
   927→    }
   928→});
   929→
   930→// ============================================================================
   931→// EXPORTS
   932→// ============================================================================
   933→
   934→// Export to window for use by other modules
   935→window.SpliceCompatibility = SpliceCompatibility;
   936→

